INSERT INTO `categories` VALUES (1, NULL, 'کتاب', 'commodi-sit-quo-magnam', 'را روی هر سه نسخه خالی بود. پیدا بود که تحویل گیرنده باید پرشان کند. همین کار را می‌برید و پیش می‌رفت. در زندگی.', 'cupiditate-ad-et-maiores-voluptatibus-voluptas-soluta/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (2, 1, 'dolorem', 'enim-veritatis-et-ut-voluptatem-quisquam-assumenda-nihil-qui', 'و آبادانی و آن وقت میتینگم را برایش تعریف کردم که ناظم دهانش آب افتاده است. و من و ناظم را خواستم. معلوم شد آن.', 'omnis-in-rerum-voluptatem-dolore-corrupti-ipsum/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (3, 1, 'omnis', 'aliquid-dignissimos-quas-natus', 'داده‌ام و حالا هم ماه دوم سال بود. اواخر آبان. حالیش کردم که بد و بی‌راهی نگفتی! که از شر چیزی خلاص شده است که.', 'modi-eos-doloribus-nemo-velit-atque-cum-nulla/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (4, 1, 'aspernatur', 'quo-eveniet-ut-voluptatem-est-libero-at-officia', 'سیگار براشون بخرم، فردا می‌فرستنم سراغ عرق. من این‌ها رو می‌شناسم. راست می‌گفت. زودتر از بچه‌ها صورتش مثل.', 'sunt-earum-esse-odit-quasi-eos/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (5, NULL, 'aut', 'ducimus-saepe-rem-id-cum', 'می‌کرد که شبیه التماس بود و پیدا بود که مراعات کرده بودند که از دست او و بچه‌اش را توی جیبم گذاشتم و قرار شد.', 'quia-et-similique-esse-iure/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (6, NULL, 'quia', 'mollitia-in-minus-ea-qui-corporis-aperiam-laboriosam', 'برای مستراح‌ها دو روزه در بسازد که ناظم همان شب روی خشت نشسته بوده و پسرش هم به زور گرفته بودم. سنگ‌هامان را.', 'doloribus-vel-omnis-quibusdam/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (7, NULL, 'dolor', 'nostrum-non-autem-eum-ex-recusandae-qui-saepe-dolorem', 'چایی‌ام را که باز است و مثقالی هفت صنار با دیگران فرق دارد و این کار بشکند. خارج از مرکز می‌دادند. با حقوق ماه.', 'animi-quia-amet-voluptates-provident-aliquam/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (8, NULL, 'sequi', 'exercitationem-qui-repellendus-natus-repellat', 'و تیپی داشت؟ و راستش تصمیم گرفتم که از حفظ کرده بود و از این حرف‌ها قباحت داره. معلم جماعت اجر دنیایی ندارد،.', 'ullam-accusamus-quod-qui-necessitatibus-vitae/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (9, NULL, 'sunt', 'consequatur-illo-qui-repellat-et', 'در این فکر بودم که با این جمله به حد اعلا رساندم که: - اگر مهر هم بایست زد، خودت بزن بابا. و رفتم سراغ اتاق خودم..', 'at-nam-nihil-quis-pariatur-illo-dolor-quis/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
INSERT INTO `categories` VALUES (10, NULL, 'incidunt', 'maiores-dignissimos-dolor-excepturi-ab-laudantium-non', 'آدم حسابی. توی دفتر، اولین چیزی که به دیوار کوبیده بود پس زد و: - مگه نفهمیدین آقا؟ مخصوصاً جاش رو خالی گذاشته.', 'atque-ut-fugit-vel-ipsa-nihil-facilis-vero-voluptatibus/', 1, 1, '2022-11-05 18:13:49', '2022-11-05 18:13:49', NULL, NULL);
