INSERT INTO `creators` VALUES (4, NULL, NULL, 'الیوت آرنولد', '1', 'متولد: 1912 میلادی در آمریکا\r\n\r\nکتاب <a href=\"http://mehrak.bankwp.com/product/%d8%a7%d8%b3%d9%84%d8%ad%d9%87-%d8%b3%d8%b1%db%8c/\">اسلحه سری</a> اثری از آرنولد است که با ترجمه مجید عمیق در نشر مهرک به چاپ رسیده است.\r\n\r\nافتخارات:\r\n\r\nالیوت آرنولد بیشتر به‌خاطر رمانش در سال 1947 معروف به خون برادر شناخته شده است که با عنوان فیلم تحسین‌برانگیز 1950 براک آروم و یک سریال تلویزیونی 1956 با همین نام اقتباس شد.', NULL, NULL, '2023-02-02 10:55:38', '2023-02-02 10:55:38', NULL, NULL);
INSERT INTO `creators` VALUES (5, NULL, NULL, 'پاول مار', '1', 'متولد: 1937 آلمان\r\n\r\nکتاب <a href=\"http://mehrak.bankwp.com/product/%d9%82%d8%b7%d8%a7%d8%b1-%d9%88-%d9%85%d8%a7%d8%af%d8%b1-%d8%a8%d8%b2%d8%b1%da%af/\">قطار و مادربزرگ</a> نوشته پاول مار است که با ترجمه کمال بهروز کیا در نشر مهرک به چاپ رسیده است.', NULL, NULL, '2023-02-02 10:55:39', '2023-02-02 10:55:39', NULL, NULL);
