INSERT INTO `creators` VALUES (35, NULL, NULL, 'گامایون', NULL, 'گامایون نام مستعار سیف‌الدین همایون فرخ است.\r\n\r\nآثار : رمان‌ها و مجموعه داستان‌ها و کتاب‌های ویژة کودکان و نوجوانان و حتی داستان‌های کوتاه و مصوّر برای خردسالان است. او چند کتاب علمی برای عموم و یا داستان و خاطرات علمی هم ترجمه کرده است.', NULL, NULL, '2023-02-02 10:56:44', '2023-02-02 10:56:44', NULL, NULL);
INSERT INTO `creators` VALUES (36, NULL, NULL, 'ابراهیم حسن‌بیگی', NULL, 'متولد: 1336 در بندرترکمن\r\n\r\nاز حسن بیگی کتاب <a href=\"http://mehrak.bankwp.com/product/%d8%b1%d9%88%d8%a7%db%8c%d8%aa%db%8c-%d8%b3%d8%a7%d8%af%d9%87-%d8%a7%d8%b2-%d9%85%d8%a7%d8%ac%d8%b1%d8%a7%db%8c%db%8c-%d9%be%db%8c%da%86%db%8c%d8%af%d9%87/\">روایتی ساده از ماجرایی پیچیده</a> در نشر مهرک به چاپ رسیده است.\r\n\r\nافتخارات :\r\n\r\nحسن بیگی دارای 43 اثر برگزیده در جشنواره‌های مختلف در ایران ازجمله جشنواره کتاب سال، کتاب فصل، کتاب دفاع مقدس، جشنواره شهید غنی پور، جشنواره کانون پرورش فکری کودکان و نوجوانان، کتاب جهانی سال جمهوری اسلامی، کتاب سال تقریب مذاهب.\r\n\r\nجایزه هنری غدیر و… با کتاب‌های محمد، اشکانه، قدیس، صوفی و چراغ جادو، امیرحسین و چراغ جادو است.\r\n\r\nهمچنین کتاب غنچه بر قالی او کتاب برگزیده سال 2000 از سوی کتابخانه ملی مونیخ المان شد.\r\n\r\nاو در سال 1378 موفق به اخذ گواهینامه درجه دو هنر از سوی وزارت فرهنگ و ارشاد اسلامی شد.', NULL, NULL, '2023-02-02 10:56:44', '2023-02-02 10:56:44', NULL, NULL);
