INSERT INTO `creators` VALUES (44, NULL, NULL, 'احمد خدادوست', NULL, 'متولد: 1338 در گیلان\r\n\r\nآثار:\r\n\r\nهیئت گل‎‌ها\r\n\r\nدر دبستان بهار\r\n\r\nپا به پای گل‌ها\r\n\r\nافتخارات:\r\n\r\nتاکنون از او بیش از 30 عنوان کتاب شعر منتشر شده است.\r\n\r\nاو علاوه بر اینکه کارشناس مرکز آفرینش‌های ادبی کانون پرورش فکری گیلان است، در مجامع و فعالیت‌های حوزه شعر کودک و نوجوان حضوری شاخص دارد.\r\n\r\nخدادوست، همچنین با مجلات کودکان و نوجوانان از جمله کیهان بچه‌ها همکاری دارد و اشعارش در این نشریه به چاپ می‌رسد.', NULL, NULL, '2023-02-02 10:56:47', '2023-02-02 10:56:47', NULL, NULL);
INSERT INTO `creators` VALUES (45, NULL, NULL, 'احمد دهقان', NULL, 'متولد: 1345 در کرج\r\n\r\nآثار:\r\n\r\nبچه های کارون\r\n\r\nآزادی خرمشهر\r\n\r\nسفر به گرای 270 درجه\r\n\r\nافتخارات :\r\n\r\nجایزه جشنواره بیست سال ادبیات داستانی وزارت فرهنگ و ارشاد اسلامی برای نوشتن رمان سفر به گرای ۲۷۰ درجه\r\nجایزه بیست سال ادبیات پایداری برای رمان سفر به گرای ۲۷۰ درجه', NULL, NULL, '2023-02-02 10:56:48', '2023-02-02 10:56:48', NULL, NULL);
