INSERT INTO `creators` VALUES (48, NULL, NULL, 'اسدالله شعبانی', NULL, 'متولد: 1337 در همدان\r\n\r\nمجموعه هل ام بده قل ام بده از آثار این نویسنده در نشر مهرک است.\r\n\r\nافتخارات:\r\n\r\nبزرگترین و مهم‌ترین منبع شعر خردسالان در ایران راه یافته به جشنوارهٔ رشد، سال ۱۳۹۴\r\n«پرسه‌های شبانه» کتاب تقدیری جشنوارهٔ کتاب برتر، سال ۱۳۹۴\r\n«یک نفر رد شد از کنار دلم»، مجموعه شعر برای نوجوانان و جوانان، کتاب برتر پنجمین جشنواره انجمن فرهنگی ناشران ایران، سال ۱۳۸۸\r\n«ساز من ساز جیرجیرک»، کتاب برگزیده نومای ژاپن و جشنواره‌های ملی و بین الملی دیگر از جمله شورای کتاب کودک\r\n«آواز توکا»، کتاب سال مجله پوپک برگزیده جشنواره کانون پرورش فکری کودکان\r\n«عصا و اسب چوبی»، کتاب منتخب بررسی کتاب کانون پرورش فکری کودکان و نوجوانان\r\n«ماه نو، نگاه نو»، کتاب سال وزارت ارشاد اسلامی، ترجمه شده به زبان مالی', NULL, NULL, '2023-02-02 10:56:48', '2023-02-02 10:56:48', NULL, NULL);
