INSERT INTO `creators` VALUES (55, NULL, NULL, 'اکبر صحرایی', NULL, 'متولد: 1339 شیراز\r\n\r\nآثار: فرار به جابلقا\r\n\r\nبمب کلاغی\r\n\r\nخمپاره های نقلی\r\nمین سوسکی\r\n\r\nتویوتای خرگوشی\r\n\r\nگردان بلدرچین\r\n\r\nو ....\r\n\r\nافتخارات:\r\n\r\nصحرایی با نگارش رمان حافظ هفت در پنجمین دوره جایزه ادبی جلال آل احمد عنوان برگزیده را به دست آورد.\r\n\r\nدر پنجمین جشن فرهنگ فارس، با مجموعه داستان «خمپاره‌های خواب‌آلود»، جایزه اول داستان را کسب کرد.', NULL, NULL, '2023-02-02 10:56:51', '2023-02-02 10:56:51', NULL, NULL);
