INSERT INTO `creators` VALUES (57, NULL, NULL, 'امیر خجسته', NULL, 'متولد: سال 1367 در تهران\nامیر خجسته تصویرگر خلاق مجموعه روزهایی که پنج وجبی یک زرافه کوچولو بود که از نشر مهرک چاپ شده است.\nافتخارات:\nبرنده‌ی نخستین مسابقه پرسبوک ایران در سال ۱۳۸۹\nجایزه‌ی دوم مسابقه‌ی تصویر استاندارد، تهران ۱۳۸۹ شده است', NULL, NULL, '2023-02-02 10:56:51', '2023-02-02 10:56:51', NULL, NULL);
