INSERT INTO `creators` VALUES (59, NULL, NULL, 'امیر شعبانی پور', NULL, 'متولد: 1358 در فومن\r\n\r\nآثار:\r\n\r\nکوه مرا صدا زد\r\n\r\nچوپان کوچک\r\n\r\nافتخارات:\r\n\r\nدیپلم افتخار از نمایشگاه سالانه تصویرگری کتاب کودک و نوجوان بولونیا- ایتالیا 2007، 2009، 2013، 2018، 2020\r\n\r\nدیپلم افتخار از مسابقه «تصویر برای کتاب» سالانه تصویرسازی کتاب مسکو- روسیه 2019\r\n\r\nجایزه جزیره سبز از نمایشگاه بین المللی نامی کره جنوبی – جزیره نامی 2017\r\n\r\nدیپلم افتخار و جایزۀ سوم هنر جوان- تهران 1386\r\n\r\n&nbsp;', NULL, NULL, '2023-02-02 10:56:52', '2023-02-02 10:56:52', NULL, NULL);
INSERT INTO `creators` VALUES (60, NULL, NULL, 'امیر علایی', NULL, 'متولد: 1363 در تهران\r\n\r\nآثار :\r\n\r\nمنطقه مرده\r\n\r\nبرگ های چند رنگ باغ\r\n\r\nرازهای سانتی متری\r\n\r\nاروند ما را با خود می برد\r\n\r\nافتخارات:\r\n\r\nنفر برگزیده تصویرگری کتاب کودک شانزدهمین جشنواره کتاب کودک و نوجوان، سال ١٣٩٢\r\n\r\nبرگزیده نمایشگاه دوسالانه تصویرگری تهران در بخش داستانى،سال 1392\r\n\r\nبرگزیده نمایشگاه بین المللی تصویرگری شارجه امارات ٢٠١٣، ٢٠١٤ و ٢٠١٥ در بخش تصویر سازی.\r\n\r\n&nbsp;', NULL, NULL, '2023-02-02 10:56:52', '2023-02-02 10:56:52', NULL, NULL);
INSERT INTO `creators` VALUES (61, NULL, NULL, 'امیر مشهدی عباس', NULL, 'متولد: 1359 در تهران\r\n\r\nامیر عباس مشهدی تصویرگر خلاق کتاب نجات آهو است که در نشر مهرک چاپ شده است.\r\n\r\nآثار:\r\n\r\nفیتیله، جمعه تعطیله\r\n\r\nافتخارات:\r\n\r\nاز فعالیت‎های وی می‎توان به عضویت در هیأت موسس و هیات مدیره انجمن تئاتر کودکان و نوجوانان خانه تئاتر ایران\r\n\r\nمدیر اولین کارگاه تخصصی تئاتر کودک و نوجوان دانشگاه تهران (تالار مولوی) و سال‎ها تدریس در مراکز مختلف اشاره کرد.', NULL, NULL, '2023-02-02 10:56:52', '2023-02-02 10:56:52', NULL, NULL);
INSERT INTO `creators` VALUES (62, NULL, NULL, 'امیر مفتون', NULL, 'متولد: 1361 در تهران\r\n\r\nآثار:\r\n\r\nکتاب سنگ سلام\r\nکتاب مردی که می‌خواست زن بگیرد\r\nکتاب کودک باران می‌بارد\r\nمجموعه داستان‌های محمود حکیمی\r\nکتاب گردان قاطرچی‌ها\r\nکتاب جغد صبح\r\n\r\nافتخارات:\r\n\r\nشروع فعالیت حرفه‌ای هنری از سال ۱۳۷۹ همکاری با نشریات و مجلات از جمله: شباویز، پیدایش، انتشارات مدرسه، محراب قلم و ...', NULL, NULL, '2023-02-02 10:56:53', '2023-02-02 10:56:53', NULL, NULL);
INSERT INTO `creators` VALUES (63, NULL, NULL, 'امیر نجفی', NULL, 'متولد: سال 1359 در کرج\r\nامیر نجفی گرافیست خلاق مجموعه داستان سه جلدی بیزبیزپشه، روباه و سه دختر و ... است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\n\r\nشرکت در دومین دوسالانه‌ی پوستر جهان اسلام\r\n\r\nشرکت در دهمین بینال سفال و سرامیک ملی ایران، موزه امام علی(ع) تهران\r\n\r\nشرکت در اولین جشنواره فرهنگی هنری ایران در دانمارک، کپنهاگ، بخش پوستر\r\n\r\nهنرمند مدعو در نمایشگاه گروهی «رهایی غزه» فرهنگسرای نیاوران، بخش مجسمه\r\n\r\nنمایشگاه انفرادی نقاشیخط، نگارستان شاهد، تهران\r\n\r\nکسب مقام اول بخش نقاشیخط از نوزدهمین جشنواره ملی قرآن دانشجویان ایران\r\n\r\nکسب مقام اول طراحی پوستر از سومین دوسالانه نهج البلاغه دانشگاهیان ایران', NULL, NULL, '2023-02-02 10:56:53', '2023-02-02 10:56:53', NULL, NULL);
INSERT INTO `creators` VALUES (64, NULL, NULL, 'امیرحسین انبارداران', NULL, 'متولد: 1348 در قم\r\nآثار:\r\n\r\nپلاک و بازوبند\r\n\r\nسه شنبه هایی از جنس ماه\r\n\r\nامیر دلاور\r\n\r\nافتخارات:', NULL, NULL, '2023-02-02 10:56:53', '2023-02-02 10:56:53', NULL, NULL);
INSERT INTO `creators` VALUES (65, NULL, NULL, 'امیرحسین فردی', NULL, 'متولد:1328 در اردبیل\r\n\r\nآثار:\r\n\r\nاسماعیل\r\n\r\nگرگسالی\r\n\r\nسیاه چمن\r\n\r\nآشیانه در مه\r\n\r\nافتخارات:\r\n\r\nعضویت در حوزه اندیشه و هنر اسلامی، سردبیری و مدیرمسئولی «کیهان بچه‌ها»\r\n\r\nعضویت در شورای داستان کانون پرورش فکری کودکان و نوجوانان، عضویت در شورای داستان بنیاد حفظ آثار و نشر ارزشهای دفاع مقدس\r\n\r\nمسئولیت جشنواره انتخاب کتاب سال شهید حبیب غنی‌پور', NULL, NULL, '2023-02-02 10:56:54', '2023-02-02 10:56:54', NULL, NULL);
