INSERT INTO `creators` VALUES (68, NULL, NULL, 'انوشه منادی', NULL, 'متولد: 1337 در تهران\r\n\r\nاثر: شهربانو طلا\r\n\r\nافتخارات:\r\n\r\nدریافت ديپلم ادبي و داستان نويسي برای «كره ي طلائي»، «از پس آبي زلال» و مجموعه داستان كوتاه «آبكند»، سال 1368\r\n\r\nو ....', NULL, NULL, '2023-02-02 10:56:55', '2023-02-02 10:56:55', NULL, NULL);
INSERT INTO `creators` VALUES (69, NULL, NULL, 'ایرج خان باباپور', NULL, 'متولد : \r\nایرج خان باباپور تصویرگر و طراح جلد خلاق کتاب طنابی از آتش است که توسط نشر مهرک چاپ شده است.\r\n\r\nبرخی از آثار او:\r\nطنابی از آتش\r\nهترین دوست او درس و کتاب بود\r\nکارهایش را به موقع انجام می داد\r\nناخن ها و موهایش را مرتب کوتاه می کرد\r\nنقاشی را خیلی دوست داشت\r\n\r\nافتخارات:\r\nنگارگری شاهنامه فردوسی نیز از دیگر فعالیت های این هنرمند برجسته می باشد.\r\nیکی از ابداعات بسیار قابل توجه وی استفاده از تکنیک آبرنگ پیشرفته می باشد که در تمام آثار هنری بی سابقه بوده است.', NULL, NULL, '2023-02-02 10:56:55', '2023-02-02 10:56:55', NULL, NULL);
INSERT INTO `creators` VALUES (70, NULL, NULL, 'ایرج صغیری', NULL, 'متولد: سال ۱۳۲۵ در بوشهر\r\n\r\nآثار:\r\nخالو نکیسا\r\nخلخال شوم\r\nقلندرخونه\r\nمحپلنگ\r\nخالو نکیسا\r\n\r\nافتخارات:\r\nنمایشنامه «قلندرخونه» از آثار او به عنوان اثر شایسته تقدیر بیستمین دوره جایزه کتاب فصل شناخته شد.   \r\n', NULL, NULL, '2023-02-02 10:56:55', '2023-02-02 10:56:55', NULL, NULL);
INSERT INTO `creators` VALUES (71, NULL, NULL, 'آنتوان چخوف', NULL, 'متولد: سال۱۸۶۰ در تاگانروگ \r\nآثار: \r\nنشان شیر و خورشید\r\nبانو با سگ ملوس\r\nشرط‌بندی\r\nهمسر\r\nاتاق شماره شش\r\nزندگی من\r\nداستان بلند\r\nدکتر بی‌مریض\r\nداستان ملال‌انگیز\r\n\r\nافتخارات:\r\nچخوف زندگی کوتاهی داشت و همین زندگی کوتاه همراه با بیماری بود اما بیش از ۷۰۰ اثر ادبی آفرید، او را مهم‌ترین داستان کوتاه‌نویس برمی‌شمارند و در زمینهٔ نمایش‌نامه‌نویسی نیز آثار برجسته‌ای از خود به‌جا گذاشته‌است.                                                                                                                                                                                                                 \r\n', NULL, NULL, '2023-02-02 10:56:56', '2023-02-02 10:56:56', NULL, NULL);
INSERT INTO `creators` VALUES (72, NULL, NULL, 'بابک نیک طلب', NULL, 'متولد: سال 1346 در تهران\r\nآثار:\r\nخواهرم پرنده شد\r\nهم این قشنگ هم اون قشنگ\r\nتا خیابان خوشبخت\r\nو...\r\nافتخارات:\r\nبرنده لوح زرین چهارمین دوره کتاب سال کانون پرورش فکری به کتاب پیشواز روشنی۱۳۷۱\r\nبرنده لوح سپاس سروش نوجوان ۱۳۷۱\r\nلوح شایسته تقدیر فصل بهار ۱۳۸۹ به کتاب تا خیابان خوشبخت\r\nبرنده سرو زرین پنجمین جشنواره شعر فجر ۱۳۸۹\r\nبرنده کتاب برتر جشنواره کتاب برتر انجمن فرهنگی ناشران کودک ونوجوان به کتاب با فرفره ها به شادی باد بگرد۱۳۹۴', NULL, NULL, '2023-02-02 10:56:56', '2023-02-02 10:56:56', NULL, NULL);
