INSERT INTO `creators` VALUES (74, NULL, NULL, 'بهروز اقبالی درخشان', NULL, 'متولد: سال 1367 \r\nآثار: \r\nپلاک‌ها و بی‌نشانی‌ها\r\nیک کتاب آفتابی\r\n\r\nافتخارات', NULL, NULL, '2023-02-02 10:56:57', '2023-02-02 10:56:57', NULL, NULL);
INSERT INTO `creators` VALUES (75, NULL, NULL, 'بهمن پگاه راد', NULL, 'متولد:  ۱۳۳۰ در شیراز  \r\nآثار:\r\nراز ساختمان زرد\r\nفارس\r\n\r\nافتخارات: \r\nتقدیر از ۲ کتاب «راز ساختمان زرد» و «نبرد رئیس علی»\r\nنامزدی در ۳دوره جشنواره مطبوعات، در بخش داستان کوتاه\r\nنامزدی برای ۲کتاب «فارس» از سری کتاب‌های «دوستت دارم ایران» و «پرنده و آتش»\r\nکتاب «مثل رود پرخروش» تقدیری ادبیات پایداری، وزارت فرهنگ و ارشاد اسلامی، سال۱۳۸۲\r\nکتاب «همهمه‌های زیر شیروانی» برنده تندیس سرو از اولین جشن فرهنگ فارس', NULL, NULL, '2023-02-02 10:56:57', '2023-02-02 10:56:57', NULL, NULL);
INSERT INTO `creators` VALUES (76, NULL, NULL, 'پژمان رحیمی زاده', NULL, 'متولد: سال ۱۳۴۹ در تهران \r\n\r\nپژمان رحیمی زاده طراح جلد خلاق کتاب «رونی یک پیانو قورت داده است» است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nشرکت در بیش از 70نمایشگاه گروهی نقاشی و تصویر سازی در داخل و خارج از کشور\r\nبرگزاری سه نمایشگاه انفرادی. نقاشی و تصویرسازی\r\nتصویرگری 60عنوان کتاب برای کودکان و نوجوانان\r\nطراحی و تصویرسازی بیش از 900 عنوان روی جلد کتاب و مجله\r\nدریافت جوایز متعدد از جشنواره های داخلی و خارجی', NULL, NULL, '2023-02-02 10:56:58', '2023-02-02 10:56:58', NULL, NULL);
INSERT INTO `creators` VALUES (77, NULL, NULL, 'پیام ابراهیمی', NULL, 'متولد: سال 1367  \r\nآثار: \r\nفرودگاه لطفا! \r\nکک بهلول - مجموعه شکرستان\r\nافتخارات:\r\nکتاب‌های بسیاری برای کودکان نوشته و به زبان‌های فرانسوی، کره‌ای، چینی، انگلیسی، آلمانی، روسی و ترکی ترجمه کرده است.', NULL, NULL, '2023-02-02 10:56:58', '2023-02-02 10:56:58', NULL, NULL);
