INSERT INTO `creators` VALUES (91, NULL, NULL, 'جواد محقق', NULL, 'متولد: سال 1333 در همدان\r\nآثار:\r\nقصه‌ی مرد بزرگ پاپتی\r\nمردی چو آفتاب\r\nتاوان عشق\r\nمثل من به انتظار\r\nقدر استاد\r\nیاد ماندگار\r\nو...\r\nافتخارات:\r\nداوری جشنواره‌های گوناگون کتاب و مطبوعات\r\nویراستاری ده‌ها عنوان کتاب و مجله\r\nمسئولیت صفحات شعر مجلۀ کیهان بچه‌ها\r\nعضویت در شورای طرح و برنامۀ خانۀ روزنامه‌نگاران جوان\r\nدبیر علمی بیش از 15 جشنوارۀ ملی در حوزۀ شعر، داستان، کتاب و مطبوعات\r\nعضو هیئت علمی جایزۀ کتاب سال و کتاب فصل جمهوری اسلامی ایران\r\nداوری ده‌ها جشنوارۀ کشوری انتخاب شعر، داستان، کتاب و مطبوعات\r\nعضویت در هیئت مدیره و نایب رئیس انجمن نویسندگان کودک و نوجوان انجمن قلم ایران و انجمن معلمان انشاء\r\nمسئول شورای انتخاب و خرید کتاب\r\nعضو شورای اعطای گواهینامه ادبی و هنری و عضو هیئت نظارت بر کتاب کودک و نوجوان در وزارت فرهنگ و ارشاد اسلامی', NULL, NULL, '2023-02-02 10:57:02', '2023-02-02 10:57:02', NULL, NULL);
