INSERT INTO `creators` VALUES (104, NULL, NULL, 'حسن عامه کن', NULL, 'متولد: سال 1358 در آستانه اشرفیه\r\n\r\nحسن عامه‌کن تصویرگر خلاق مجموعه هم‌ام بده قل‌ام بده است که در نشر مهرک چاپ شده است\r\n\r\nافتخارات:\r\nبرنده جشنواره تصویرسازی داستان های قرآنی ، سال ۲۰۰۴\r\nبرنده جایزه اول تصویرسازی کتاب کودک تهران، سال ۲۰۰۴ و جایزه دوم، سال ۲۰۰۵ (حوزه هنری تهران)\r\nبرنده جایزه اول تصویرسازی مجلات کودکان (مجله رشد) سال ۲۰۰۵\r\nبرنده مقام اول تصویرسازی در بخش نوجوان ، ششمین دوسالانه تصویرسازی تهران، سال ۲۰۱۴\r\nبرنده IBBY تهران با موضوع داستان های اندرسون، سال ۲۰۰۵،\r\nبرنده مقام دوم برای تصویرسازی مجلات کودکان ایران، سال ۲۰۰۴ و ۲۰۰۵\r\nبرنده مسابقه تصویرسازی بین المللی کتاب کودک ژاپن، کنکور نوما ، سال ۲۰۰۴ و ۲۰۰۵\r\nبرنده مسابقه بین المللی کارتون (Taejon) کره جنوبی ، سال ۲۰۰۱ و ۲۰۰۲\r\nبرنده جایزه سبز از گروه Namiکره جنوبی\r\nبرنده مسابقه تصویرسازی بین المللی کتاب کودک هندوستان ، جایزه Chitrakala سال ۲۰۰۵\r\nانتخاب شده به عنوان یکی از ۴۰ استاد تصویرگری از سراسر جهان در بیست وسومین نمایشگاه بین المللی Sarmedeh ایتالیا سال ۲۰۰۵ و ۲۰۱۰\r\nانتخاب شده در نمایشگاه بین المللی تصویرسازی کتاب بلونیا در ایتالیا، سال ۲۰۰۷، ۲۰۱۰ و ۲۰۱۳\r\nبرنده بین المللی جشنواره طنز چین، سال ۲۰۰۷', NULL, NULL, '2023-02-02 10:57:06', '2023-02-02 10:57:06', NULL, NULL);
