INSERT INTO `creators` VALUES (122, NULL, NULL, 'حمید قربان پور', NULL, 'متولد: سال ۱۳۶۵ در مشهد\r\n\r\nحمید قربان‌پور طراح‌گرافیک خلاق کتاب مردان نخیله، مجموعه نماشنامه بچه‌های مسجد است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\n\r\nنمایشگاه گروهی گرافیک گالری سلطان علی مشهدی، مشهد، سال۲۰۱۳\r\n\r\nنمایشگاه گروهی گالری رضوان، مشهد، سال ۲۰۱۲ و ۲۰۱۳\r\n\r\nجایزه اول جشنواره پوستر «بهار جان» مشهد، سال ۲۰۱۴\r\n\r\nجایزه اول جشنواره «خوشنویسی قرآنی» خراسان رضوی، سال۲۰۰۲ و ۲۰۰۳ و ۲۰۰۴\r\n\r\nجایزه جشنواره بین‌المللی ۹ و ۱۰ پوستر «بسم الله» تهران،سال ۲۰۱۲ و ۲۰۱۳', NULL, NULL, '2023-02-02 10:57:11', '2023-02-02 10:57:11', NULL, NULL);
