INSERT INTO `creators` VALUES (131, NULL, NULL, 'خسرو باباخانی', NULL, 'متولد: سال 1338در تهران\r\nآثار:\r\nمثل دست های مادرم\r\nگنج قلعه ی متروک\r\nویولون‌زن روی پل\r\nپشت دروازه های شب\r\nو...\r\nافتخارات:\r\nکتاب \"بر فراز گندمزار\" و مثل \"دست‌های مادرم\" برگزیده کانون پرورش فکری، برگزیده سروش نوجوان و برگزیده سوره‌ی نوجوان و حضور در مرحله نهایی کتاب سال وزارت ارشاد.\r\n\"پشت دروازه‌های شب\". جشنواره یاد یار\r\nرمان \"گنج متروک\" کتاب سال مجله سوره‌ی نوجوان\r\nو داوری های بسیار در جشنواره‌های مختلف', NULL, NULL, '2023-02-02 10:57:13', '2023-02-02 10:57:13', NULL, NULL);
