INSERT INTO `creators` VALUES (242, NULL, NULL, 'علیرضا پورحنیفه', NULL, 'متولد: سال 1362 در بندرانزلی\r\nعلیرضا پورحنیفه تصویرگر خلاق کتاب مهدکودک گربه‌ها و نمکی و مار عینکی است که از نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nبرنده جایزه هیئت داوران در مسابقه بین‌المللی تصویرگری و کاریکاتور ملانصرالدین ترکیه\r\n\r\nبرنده جشنواره مطبوعات کودک و نوجوان\r\n\r\nشرکت در نمایشگاه‌‌های سرو نقره‌ای (انجمن صنفی طراحان گرافیک ایران)\r\n\r\nشرکت در نمایشگاه طراحی جلد کتاب سوره‌مهر منتخب نمایشگاه بین‌المللی طراحی گرافیک Golden Bee (زنبور طلایی) روسیه', NULL, NULL, '2023-02-02 10:57:37', '2023-02-02 10:57:37', NULL, NULL);
