INSERT INTO `creators` VALUES (256, NULL, NULL, 'فرهاد حسن‌زاده', NULL, 'متولد: سال 1340 در آبادان\r\nآثار:\r\nنمکی و مار عینکی\r\nماجرا روباه و زنبور\r\nماشومه در مه\r\nسمفونی حمام\r\nکلاغ کامپیوتر\r\nدارکوب و کرگدن\r\nافتخارات:\r\nبرگزیده به عنوان «نویسنده محبوب بچه‌ها» در نخستین جام باشگاه‌های کتابخوانی کشور\r\nتقدیری پنجمین جشنواره مهر برای کتاب «هویج‌بستنی»، سال 1396\r\nتقدیر شده به خاطر فیلمنامه اقتباسی از کتاب «هستی» در سی‌امین جشنواره فیلم کودک و نوجوان اصفهان، سال 1396\r\nنویسنده برگزیده کتاب برتر در نهمین جشنواره کتاب برتر انجمن فرهنگی ناشران کتاب کودک برای کتاب «زیبا صدایم کن»، سال 1396\r\nتقدیری شورای کتاب کودک برای کتاب «چتری با پروانه‌های سفید»، سال 1397\r\nنامزد جایزه گلشیری برای کتاب حیاط خلوت\r\nتقدیر شده جایزه مهرگان برای کتاب حیاط خلوت\r\nنامزد جایزه قلم زرین برای کتاب حیاط خلوت\r\nنامزد کتاب سال ایران برای کتاب حیاط خلوت\r\nنامزد سال دومین جشنوارهٔ لاک‌پشت پرنده برای «این وبلاگ واگذار می‌شود.»', NULL, NULL, '2023-02-02 10:57:39', '2023-02-02 10:57:39', NULL, NULL);
