INSERT INTO `creators` VALUES (318, NULL, NULL, 'محمد حمزه‌زاده', NULL, '<h5>متولد: سال 1348 در تهران</h5>\r\nآثار:\r\n\r\nچشم چشم دو ماهی\r\n\r\nخواب‌هایب عجیب\r\n\r\nبابا لنگ دراز\r\n\r\nآهو جان\r\n\r\nو ...\r\n\r\nافتخارات:\r\n\r\nعضو هیئت مدیره انتشارات مدرسه\r\n\r\nمعاون دفتر کودک و نوجوان ادبیات و هنر مقاومت حوزه هنری\r\n\r\nسردبیر هفته نامه مهر\r\n\r\nرئیس هیئت مدیره و مدیر عامل شرکت مطبوعاتی سوره\r\n\r\nقائم مقام حوزه هنری\r\n\r\nعضو هیئت مدیره انجمن قلم ایران (یک دوره)\r\n\r\nمدیر عامل شرکت انتشارات سوره مهر\r\n\r\nدبیر شورای راهبردی و سردبیر ماهنامه سوره\r\n\r\nسردبیر سایت ادبی، هنری لوح', NULL, NULL, '2023-02-02 10:57:49', '2023-02-02 10:57:49', NULL, NULL);
