INSERT INTO `creators` VALUES (352, NULL, NULL, 'محمدرضا بایرامی', NULL, 'متولد: سال 1340 در اردبیل\nآثار:\nکوه مرا صدا زد\nلم یزرع\nپس از کشتار\nمردگان باغ سبز\nو...\nافتخارات:\nجایزۀ ادبی جلال برای کتاب لم یزرع.\nجایزۀ کتاب سال جمهوری اسلامی ایران برای کتاب لم یزرع.\nجایزۀ کتاب سال جمهوری اسلامی ایران برای کتاب گرگ‌ها از برف نمی‌ترسند.\nجایزه خرس طلایی و جایزه کبرای آبی و نیز جایزه کتاب سال سوییس برای کتاب کوه مرا صدا زد.', NULL, NULL, '2023-02-02 10:57:53', '2023-02-02 10:57:53', NULL, NULL);
