INSERT INTO `creators` VALUES (356, NULL, NULL, 'محمدرضا شرفی خبوشان', NULL, 'متولد: سال 1357 در ورامین\nآثار:\nعاشقی به سبک ون گوک\nموهای تو خانه‌ی ماهی هاست\nبالای سر آب‌ها\nاز واژه‌ها تهی\nو...\nافتخارات:\nجایزه جلال بابت بی‌کتابی\nجایزه بهترین اثر در بخش رمان نوجوان جشنواره داستان انقلاب بابت موهای تو خانه ماهی‌هاست\nشایسته تقدیر در جشنواره شهید غنی‌پور بابت رمان بی‌کتابی\nجایزه بهترین اثر در بخش رمان بزرگسال جشنواره داستان انقلاب بابت رمان روایت دلخواه پسری شبیه سمیر\nجایزه جلال بابت عاشقی به سبک ونگوگ (نامزد)\nجایزه قلم زرین بابت نامت را بگذار وسط این شعر (نامزد)', NULL, NULL, '2023-02-02 10:57:54', '2023-02-02 10:57:54', NULL, NULL);
INSERT INTO `creators` VALUES (357, NULL, NULL, 'محمدرضا اصلانی', '1', '', NULL, NULL, '2023-02-02 10:57:54', '2023-02-02 10:57:54', NULL, NULL);
INSERT INTO `creators` VALUES (358, NULL, NULL, 'محمدرضا شمس', NULL, 'متولد: سال 1336 در اردبیل\nآثار:\nقصه‌های بترس برای بچه‌های نترس\nقصه‌های الفبا\nموج اف_ ام\nکرم ابریشم\nو...\nافتخارات:\nتاب دیوانه و چاه به عنوان کتاب سال شورای کتاب کودک در بخش قصه انتخاب شد.\nکتاب دیوانه و چاه در سال 2004 دیپلم افتخار IBBY را از آن خود کرد. این اثر به فهرست کتابخانه‌ی مونیخ نیز اضافه شد.\nکتاب یک اسم و چند قصه در سال 1391 به عنوان کتاب فصل وزارت ارشاد اعلام شد.\nکتاب یک اسم و چند قصه در سال 1391 به عنوان کتاب سال جایزه‌ی شهید حبیب غنی‌پور و کتاب سال جشنواره‌ی رشد اعلام شد.\nمحمدرضا شمس دیپلم افتخار نویسنده‌ی برتر چهل سال اخیر را از سوی مرکز مطالعات ادبیات کودک دانشگاه شیراز دریافت کرده است.\nکتاب من، زن بابا و دماغ بابام کتاب برگزیده‌ی ششمین دوره‌ی جشنواره‌ی ادبی اصفهان شد.', NULL, NULL, '2023-02-02 10:57:55', '2023-02-02 10:57:55', NULL, NULL);
