INSERT INTO `creators` VALUES (363, NULL, NULL, 'محمدرضا شمس', '1', 'متولد: سال 1336 در اردبیل\nآثار:\nقصه‌های بترس برای بچه‌های نترس\nقصه‌های الفبا\nموج اف_ ام\nکرم ابریشم\nو...\nافتخارات:\nتاب دیوانه و چاه به عنوان کتاب سال شورای کتاب کودک در بخش قصه انتخاب شد.\nکتاب دیوانه و چاه در سال 2004 دیپلم افتخار IBBY را از آن خود کرد. این اثر به فهرست کتابخانه‌ی مونیخ نیز اضافه شد.\nکتاب یک اسم و چند قصه در سال 1391 به عنوان کتاب فصل وزارت ارشاد اعلام شد.\nکتاب یک اسم و چند قصه در سال 1391 به عنوان کتاب سال جایزه‌ی شهید حبیب غنی‌پور و کتاب سال جشنواره‌ی رشد اعلام شد.\nمحمدرضا شمس دیپلم افتخار نویسنده‌ی برتر چهل سال اخیر را از سوی مرکز مطالعات ادبیات کودک دانشگاه شیراز دریافت کرده است.\nکتاب من، زن بابا و دماغ بابام کتاب برگزیده‌ی ششمین دوره‌ی جشنواره‌ی ادبی اصفهان شد.', NULL, NULL, '2023-02-02 10:57:55', '2023-02-02 10:57:55', NULL, NULL);
INSERT INTO `creators` VALUES (364, NULL, NULL, 'محمدکاظم مزینایی', NULL, 'متولد: سال 1342 در دامغان\nآثار:\nشاه بی‌شین\nپسری که تنها بود\nرازهای زندگی یک کلاغ\nماه در گهواره\nپاییز در قطار\nو...\nافتخارات:\nبرگزیده‌ی جشنواره‌ی شهید غنی پور، پاییز در قطار، 1385\nتقدیر شده در اولین جشنواره‌ی داستان انقلاب، شاهِ بی شین (درباره محمدرضا و فرح پهلوی) 1386\nبرگزیده جشنواره شهید غنی‌پور، رمان شاه بی شین، 1390\nکتاب سال دفاع مقدس، منظومه‌ی سه گانه‌ی: 1- ساعت هفت و ربع 2- کتاب و آب ممنوع 3- بوی هلو و باروت، 1387\nدیپلم افتخار کتاب فصل، تابستان 1388، کلیله و دمنه\nشاعر برگزیده جشنواره شعر فجر، 1388\nبرگزیده‌ی جشنواره‌ی داستان انقلاب، رمان آه با شین، 1390\nبرگزیده جشنواره قلم زرین، رمان آه با شین، 1393\nبرگزیده جشنواره جلال آل احمد، رمان آه با شین', NULL, NULL, '2023-02-02 10:57:55', '2023-02-02 10:57:55', NULL, NULL);
