INSERT INTO `creators` VALUES (369, NULL, NULL, 'محمود برآبادی', '1', 'متولد: سال 1331 در سبزوار\nآثار:\nحماسه کوچی\nآتش زیر خاکستر\nبالاییی‌ها و پایینی‌ها\nیادهای کودکی\nجوجه تنبل\nداستان‌های تاریخی\nلبخند با بزرگان\nکارمند نمونه\nافتخارات:\nانتشار نزدیک به ۵۰ اثر در حوزه ادبیات کودک\nعضو هیأت مدیره انجمن نویسندگان کودک و نوجوان\nرئیس و نایب رئیس انجمن در یک دوره\nداور کتاب سال کودک و نوجوان در دو دوره', NULL, NULL, '2023-02-02 10:57:56', '2023-02-02 10:57:56', NULL, NULL);
INSERT INTO `creators` VALUES (370, NULL, NULL, 'محمود پوروهاب', NULL, 'متولد: سال 1340 در گیلان\r\nآثار:\r\nزبان گنجشک ها را بلد است\r\nشاعران آشنا\r\nدارد هوای تازه می‌آید\r\nماهی رود توام\r\nباید دریایی باشد ...\r\nافتخارات:\r\nدر کارنامه ادبی‌اش نقد و بررسی آثار شاعران جوان، داوری جشنواره‌های ادبی کودک و نوجوان، مسؤلیت بخش شعر بعضی از ماهنامه‎‌ها دیده می‌شود.', NULL, NULL, '2023-02-02 10:57:57', '2023-02-02 10:57:57', NULL, NULL);
