INSERT INTO `creators` VALUES (380, NULL, NULL, 'مسعود کشمیری', NULL, 'مسعود کشمیری تصویرگر خلاق مجموعه دارودسته دارعلی است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nهفتمین جشنواره هنرهای تجسمی جوانان، لوح تقدیر، سال ۱۳۷۳\r\nهشتمین جشنواره سراسری هنرهای تجسمی جوانان، هنرمند برتر، سال ۱۳۷۴\r\nدومین جشنواره کشوری تاتر دانشگاه آزاد، لوح سپاس، سال ۱۳۷۴\r\nنهمین جشنواره سراسری هنرهای تجسمی جوانان، لوح سپاس، سال ۱۳۷۵\r\nنفر اول فستیوال بین المللی هنرهای تجسمی با موضوع تبعیض، سال ۱۳۷۹\r\nنفر اول فستیوال هنرهای تجسمی دانشجویان کشور، سال ۱۳۸۰\r\nلوح تقدیر از پنجمین بینال بین المللی کارتون تهران در بخش طراحی پرتره، سال۱۳۸۰\r\nپایان نامه برتر سال در هفته پژوهش و لوح تقدیر برای فیلم انیمیشن نقاش و پرنده، سال ۱۳۸۲\r\nاز تهران تا لندن با انیمیشن، منتخب ۱۰ فیلم انیمیشن ایرانی در هفته انیمیشن های جهان در لندن، سال ۱۳۸۴\r\nسفری با انیمیشن در سینما فرهنگ، نمایش فیلم نقاش و پرنده به همراه نقد و بررسی، سال ۱۳۸۵\r\nدریافت لوح تقدیر از دانشکده هنر دانشگاه سمنان، سال ۱۳۸۷\r\nدریافت لوح تقدیر از کانون زبان ایران جهت همکاری در پروژه کتاب های انگلیسی و عربی، سال ۱۳۸۳ و ۱۳۸۴\r\nهنرمند منتخب، چاپ آثار و معرفی در کتاب کارینگاه، سال ۱۳۸۷\r\nمصاحبه با مجله Kaltblut آلمان، سال ۲۰۱۴', NULL, NULL, '2023-02-02 10:57:58', '2023-02-02 10:57:58', NULL, NULL);
