INSERT INTO `creators` VALUES (397, NULL, NULL, 'مهدی مردانی', '1', 'متولد: سال ۱۳۵۷ در قزوین \r\n\r\nآثار: \r\nجنگ سال‌هاست که تمام نشده! \r\nباران در خیمه\r\nپروانه پنهان\r\nپشت پردۀ ملکوت\r\nپروازهای پرپر\r\n\r\nافتخارات:\r\n', NULL, NULL, '2023-02-02 10:58:01', '2023-02-02 10:58:01', NULL, NULL);
