INSERT INTO `creators` VALUES (399, NULL, NULL, 'مهدی میرکیایی', '1', 'متولد: سال 1352 در تهران\r\nآثار:\r\nراهپیمایی اشک ها\r\nصلیب خونین\r\nگرگ ها با چشم باز می خوابند\r\nشکار انسان\r\nسفر به آن سوی دریاها\r\nپنج رئیس کوتوله\r\nو...\r\nافتخارات:\r\nتقدیر هشتمین جشنواره کتاب کودک و نوجوان(رد پای آتش)\r\nتقدیر سومین جشنواره مطبوعات کودک و نوجوان(دزدی که خانه اش بوی یاس می داد)\r\nبرگزیده ویژه جشنواره بانوی فرهنگ، سال 1386 (قالی و قناری)\r\nبرگزیده جشنواره بهترین کتاب،اتحادیه ناشران، سال1385(پادشاه کوتوله و چهل دردسر بزرگ)\r\nبرگزیده جشنواره کتاب های کمک آموزشی رشد، سال1386\r\nتقدیر در کتاب سال(بیایید داستان بنویسیم)، سال1395\r\nبرنده لوح زرین هجدهمین جشنواره کتاب، سال 1395(بیایید داستان بنویسیم)', NULL, NULL, '2023-02-02 10:58:01', '2023-02-02 10:58:01', NULL, NULL);
INSERT INTO `creators` VALUES (400, NULL, NULL, 'مهرداد قرآن کیش', NULL, 'متولد: سال 1392 در تهران\r\nآثار:\r\nتیر باران گناه\r\nموش کوچولو\r\nکلیله و دمنه\r\nشیر و شغال\r\nیکی بود یکی نبود\r\nافتخارات:\r\nنمایش کودک\" دویدم و دویدم\" منتخب جشنواره ترافیک معاونت شهرداری تهران اجرا در سال 92 و93 در مدارس تهران/ نویسنده و کارگردان\r\nنمایش برگزیده در جشنواره بین اللملی کودک و خانواده جزیره کیش نوروز 1394-\r\nاجرای نمایش های کودک در دومین جشنواره بین اللملی جزیره کودک، نوروز 1395/ جزیره  کیش-\r\nنمایش \" زیر گنبد کبود \" نمایش منتخب جشنواره کودک و نوجوان حوزه هنری تهران و برنده جایزه بهترین طراحی عروسک و عروسک گردانی-', NULL, NULL, '2023-02-02 10:58:01', '2023-02-02 10:58:01', NULL, NULL);
