INSERT INTO `creators` VALUES (404, NULL, NULL, 'موسی بیدج', NULL, 'متولد: سال ۱۳۳۵ در گیلانغرب \r\n\r\nآثار: \r\nباد خانه من است، گنجشك\r\nبهان\r\nتأثیر فرهنگ اسلامی بر ادبیات روسیه\r\nعصای آبنوس \r\nنگاهی به خویش\r\nگهواره‌ای برای شاعران\r\n\r\nافتخارات:\r\nاز پایه‌گذاران مجله شعر حوزه هنری و مجله طلایه بوده است\r\nنویسنده و سردبیر مجله شیراز (درباره ادبیات ایران به زبان عربی) \r\nسردبیر فصلنامه سلسله الحوار (فصلنامه فرهنگ، هنر و ادبیات ایران برای جهان عرب)\r\n\r\n\r\n', NULL, NULL, '2023-02-02 10:58:02', '2023-02-02 10:58:02', NULL, NULL);
INSERT INTO `creators` VALUES (405, NULL, NULL, 'موسی عربی', '1', 'متولد: سال 1363 در یاسوج\r\n\r\nموسی عربی مترجم کتاب نذری پرپرک است که توسط نشر مهرک چاپ شده است.\r\n\r\nآثار :\r\nنذری پرپرک\r\n', NULL, NULL, '2023-02-02 10:58:02', '2023-02-02 10:58:02', NULL, NULL);
INSERT INTO `creators` VALUES (406, NULL, NULL, 'میرشمس‌الدین فلاح‌هاشمی‌نسب', NULL, 'متولد: سال 1351 در گیلان\r\n\r\nمیرشمس‌الدین فلاح‌هاشمی‌نسب نویسنده کتاب اولین مشت از مجموعه روزهای انقلاب است که توسط نشر مهرک چاپ شده است.\r\n\r\nآثار: \r\nاولین مشت - مجموعه زورهای انقلاب\r\n\r\n', NULL, NULL, '2023-02-02 10:58:02', '2023-02-02 10:58:02', NULL, NULL);
INSERT INTO `creators` VALUES (407, NULL, NULL, 'نادر ابراهیمی', '1', 'متولد: سال ‌۱۳۱۵ در تهران \r\nنادر ابراهیمی نویسنده کتاب پدر چرای توی خانه مانده است، جای او خالی و ... است که در نشر مهرک چاپ شده است.\r\n\r\nآثار:\r\nخانه‌ای برای شب\r\nآتش بدون دود\r\nیک عاشقانه آرام\r\nصدای صحرا \r\n\r\nافتخارات:\r\nابراهیمی در زمینه‌ ادبیات کودکان، جایزه‌ نخست براتیسلاوا، جایزه‌ نخست تعلیم و تربیت یونسکو، جایزه‌ کتاب برگزیده‌ سال ایران و چندین جایزه‌ دیگر را هم دریافت کرده است. \r\nعنوان “نویسنده‌ برگزیده‌ ادبیات داستانی ۲۰ سال بعد از انقلاب” را به‌خاطر داستان بلند و هفت‌جلدی “آتش بدون دود” به‌دست آورده است. \r\nنادر ابراهیمی چندین فیلم مستند و سینمایی و هم‌چنین ۲ مجموعه‌ تلویزیونی را نوشته و کارگردانی کرده و آهنگ‌ها و ترانه‌هایی برای آن‌ها ساخته است.', NULL, NULL, '2023-02-02 10:58:02', '2023-02-02 10:58:02', NULL, NULL);
