INSERT INTO `creators` VALUES (409, NULL, NULL, 'ناصر کشاورز', '1', 'متولد: سال 1341 در تهران\r\nآثار:\r\nمن و مرغابی‌ها\r\nمداد کوتاه\r\nمجموعه کی بود کی بود\r\nاز این طرف لطفاً فقط هیس\r\nو...\r\nافتخارات:\r\n\r\nدر ۱۸ اسفند ماه ۱۳۹۵ در مراسم پایانی یازدهمین جشنواره شعر فجر نشان درجه یک هنری توسط سیدرضا صالحی امیری وزیر فرهنگ و ارشاد اسلامی وقت به وی اعطا شد.', NULL, NULL, '2023-02-02 10:58:03', '2023-02-02 10:58:03', NULL, NULL);
INSERT INTO `creators` VALUES (410, NULL, NULL, 'نقی سلیمانی', NULL, 'متولد: سال 1340 در تهران\r\nنقی سلیمانی نویسنده کتاب آخرین پیامبر است که در نشر مهرک چاپ شده است.\r\n\r\nآثار:\r\nوقتی که ماه ریفی از خربزه خیال بود\r\nگلدان پشت پنجره\r\nجناب سیب منتظر نیوتن بود\r\nافسانه نو، سروش\r\nزمین مثل دانه انگور بود\r\n\r\nافتخارات:\r\n...\r\n', NULL, NULL, '2023-02-02 10:58:03', '2023-02-02 10:58:03', NULL, NULL);
INSERT INTO `creators` VALUES (411, NULL, NULL, 'هادی حکیمیان', '1', 'متولد: سال 1347 در یزد\r\n\r\n\r\nآثار: \r\nگل‌انارها را باد خواهد برد\r\nبرج قحطی\r\n\r\nافتخارات:\r\nكسب مقام اول در دورة اول كنگرۀ شعر و قصۀ كشور در بندرعباس.\r\nكسب مقام سوم در دورة دوم كنگرۀ شعر و قصه كشور در بندرعباس.\r\nكسب رتبۀ اول در هفتمين جشنوارۀ سراسري مطبوعات كودك‌ و نوجوان.\r\nكسب رتبۀ اول در همايش عاشورا.\r\nاثر برگزيدۀ جشنوارۀ خانۀ داستان سال 80.\r\nاثر برگزيدۀ كتاب سال شهيد حبيب غني‌پور.\r\nرتبۀ برتر جشنوارۀ زاهدان.\r\n\r\n', NULL, NULL, '2023-02-02 10:58:03', '2023-02-02 10:58:03', NULL, NULL);
INSERT INTO `creators` VALUES (412, NULL, NULL, 'هادی خورشاهیان', NULL, 'متولد: سال 1352 در گنبدکاووس\nآثار:\nمن یک بادبادکم\nلطفاً آدم بزرگ‌ها پیاده شوند\nدفتر نقاشی خاطرات سایه\nچوب درخت بِه\nو...\nافتخارات:\nبرگزیده سومین دوره جشنواره شعر فجر، ۱۳۸۷\nتقدیر «پلاک ۶۱» هفتمین جایزه ادبی اصفهان، ۱۳۸۹\nرتبه سوم شعر کودک و نوجوان بیستمین کنگره دفاع مقدس، ۱۳۹۱\nگواهینامه هنری درجه دو در رشته شعر، ۱۳۹۳\nتمجید رمان «مگه من چند نفرم؟» در سیزدهمین دوره جشنواره قلم زرین، ۱۳۹۴\nتقدیر در کنگره ۲۰۰۰ شهید هنرمند «ترنم پرواز» ۱۳۹۴\nنامزدی مجموعه غزل سلام بر زندان در پانزدهمین دوره جشنواره قلم زرین، ۱۳۹۶\nنامزدی رمان الفبای مردگان در جایزه جلال آل احمد ۱۳۹۶', NULL, NULL, '2023-02-02 10:58:03', '2023-02-02 10:58:03', NULL, NULL);
INSERT INTO `creators` VALUES (413, NULL, NULL, 'هربرت گونتر', '1', 'متولد: سال 1947 در گوتینگن\r\nهربرت گونتر نویسنده کتاب اُلی اُلکان است که در نشر مهرک چاپ شده است.\r\n\r\nآثار:\r\nالی الکان \r\n\r\nافتخارات:\r\n', NULL, NULL, '2023-02-02 10:58:04', '2023-02-02 10:58:04', NULL, NULL);
INSERT INTO `creators` VALUES (414, NULL, NULL, 'وحید پورافتخاری', NULL, 'متولد: سال 1360 در تهران\r\n\r\nوحید پورافتخاری نویسنده کتاب کوزه شکسته، بچه سر راهی از مجموعه شکرستان است که در نشر مهرک چاپ شده است.\r\n\r\nآثار:\r\nگوش سفید و دم سیاه\r\nبچه سر راهی\r\n\r\nافتخارات:\r\n...\r\n', NULL, NULL, '2023-02-02 10:58:04', '2023-02-02 10:58:04', NULL, NULL);
INSERT INTO `creators` VALUES (415, NULL, NULL, 'وحید محمدی', '1', 'متولد:\r\nوحید محمدی تصویرگر خلاق کتاب قار و قار و قار کلاغ و مار، لاک پشت پرنده و ... است که در نشر مهرک چاپ شده است.\r\n\r\nآثار:\r\n\r\nافتخارات:\r\n', NULL, NULL, '2023-02-02 10:58:04', '2023-02-02 10:58:04', NULL, NULL);
INSERT INTO `creators` VALUES (416, NULL, NULL, 'یحیی علوی فرد', NULL, 'متولد: سال ۱۳۵۲ در بجنورد\r\n\r\nآثار:\r\nفصل‌ها زیبایند \r\nبهار ماندنی، \r\nنوآوری این است\r\nبیا ببین جهان ما  \r\n\r\nافتخارات:\r\nاو در سال ۲۰۰۱ برگزیده پنجمین جشنواره مطبوعات کودک و نوجوان شد. همچنین این اثر از او به زبان‌های عربی و فارسی ترجمه شده و به چاپ رسیده‌است و به عنوان کتاب سال استان قم برگزیده شد.', NULL, NULL, '2023-02-02 10:58:04', '2023-02-02 10:58:04', NULL, NULL);
INSERT INTO `creators` VALUES (417, NULL, NULL, 'یوسف سقالی', '1', 'تولد: ...\r\nیوسف سقالی نویسنده کتاب جوجه عمه ربابه است که در نشر مهرک چاپ شده است.\r\nآثار:\r\nچون دوتار\r\nعروسی مایا\r\nصیادان قره‌سو\r\nکومه‌نشین‌ها\r\nصدای زمین\r\nقصه‌نویسی به زبان ساده\r\n\r\nافتخارات: \r\n...\r\n\r\n\r\n', NULL, NULL, '2023-02-02 10:58:04', '2023-02-02 10:58:04', NULL, NULL);
INSERT INTO `creators` VALUES (418, NULL, NULL, 'یوسف قوجق', NULL, 'متولد: سال 1347 در گنبد کاووس\nآثار:\nتاریخ با طعم زغال‌اخته\nافسانه برای نوجوانان\nآلتین\nمردان فردا\nو...\nافتخارات:\nدو کتاب «لالو» و «مردان فردا» راه یافته به مرحله‌ی نهایی داوری بیست و نهمین دوره جایزه کتاب سال جمهوری اسلامی در زیرگروه داستان تألیفی بخش کودک و نوجوان در سال ۱۳۹۰\nلوح تقدیر جشنواره ملی کتاب سال کودک و نوجوان برای کتاب «مردان فردا\nلوح تقدیر سیزدهمین جشنواره رشد برای رمان «نردبانی رو به آسمان\nلوح تقدیر پانزدهمین جشنواره کتاب سال دفاع مقدس ۱۳۹۰ برای کتاب «یاد یوسف»', NULL, NULL, '2023-02-02 10:58:05', '2023-02-02 10:58:05', NULL, NULL);
INSERT INTO `creators` VALUES (419, NULL, NULL, 'محمدرضا یوسفی', '1', 'متولد: سال 1332 در همدان\nآثار:\nگاگا و مانانا\nمردی که پانزده‌ساله ماند\nادبیات کهن ادبیات نو\nکارنامه اردشیر بابکان\nگرگ‌ها گریه نمی‌کنند\nافتخارات:\nکاندیدای جایزه هانس کریستسن اندرسن از ایران در سال ۲۰۰۰ میلادی\nدریافت لوح افتخار از IBBY از هلند در سال ۱۹۹۶ میلادی\nدریافت لوح افتخار از IBBY در هندوستان به سال ۱۹۹۸ میلادی\nکتاب «دره آهوان» کتاب سال مجله سروش در سال ۱۳۷۴\nپلاک وِیژه شورای کتاب کودک به عنوان نویسنده برگزیده شال ۱۳۷۹\nرمان «وقت قصه مرا صدا کن» کتاب برگزیده انجمن ملی کتاب\nرمان «نمکی» کتاب برگزیده کانون پرورش فکری کودکان', NULL, NULL, '2023-02-02 10:58:05', '2023-02-02 10:58:05', NULL, NULL);
INSERT INTO `creators` VALUES (420, NULL, NULL, 'حمید اباذری', NULL, 'متولد: سال 1359 در اصفهان\r\nآثار:\r\nقصه‌های خواندنی فیه ما فیه مولوی\r\nزندگی‌نامه و شرح 20 غزل حافظ\r\nحکایت‌های شیرین منطق‌الطیر عطار\r\nحکایت خواندن یلیلی و مجنون\r\nافتخارات:\r\nاولین مجموعه داستانش سال ۹۰چاپ و نامزد کتاب سال شد.', NULL, NULL, '2023-02-02 10:58:05', '2023-02-02 10:58:05', NULL, NULL);
INSERT INTO `creators` VALUES (421, NULL, NULL, 'سید میثم موسوی', '1', 'متولد: سال 1363 در تهران\r\nافتخارات:\r\n1389 برگزيده نخستين جشنواره ملي هنرهاي تجسمي جوان ايراني\r\n1388 برگزيده نمايشگاه مطبوعات کودک ونوجوان\r\n1387 برگزيده نمايشگاه تصوير سازي کتاب هاي درسي\r\nتصوير سازي کتاب هاي درسي اول تا پنجم دبستان', NULL, NULL, '2023-02-02 10:58:05', '2023-02-02 10:58:05', NULL, NULL);
