INSERT INTO `creators` VALUES (461, NULL, NULL, 'پروین حیدری‌زاده', NULL, 'متولد: سال 1361در تهران\nپروین حیدری‌زاده تصویرگر خلاق کتاب گنج واقعی است که از نشر مهرک چاپ شده است.\n\nافتخارات:\nطراحی جلد کتاب شعر در پیشگاه تاناتوس\nنمایشگاه گروهی رو به نگاه من فرهنگسرای ارسباران تهران سال ۹۵٫\nنمایشگاه گروهی  داستان های مثنوی در گالری AX  برلین سال ۹۶\nنمایشگاه گروهی داستان های شاهنامه در گالری CYRUS  تورنتو سال ۹۷\nنمایشگاه گروهی به مناسبت هفته ی تصویرگری در گالری خانه هنرمندان تهران سال ۹۶٫\nنمایشگاه گروهی دو خط موازی در گالری آرتیمیس تبریز سال ۹۶\nنمایشگاه گروهی تصویرسازی به مناسبت هفته ی تصویرگری در گالری آیه دانشگاه سوره\nنمایشگاه جشنواره عاشورایی سرخ و سیاه به رنگ عشق سال ۹۵٫\nنمایشگاه جشنواره عاشورایی سوگواره در گالری برج میلاد تهران سال', NULL, NULL, '2023-02-02 10:58:13', '2023-02-02 10:58:13', NULL, NULL);
