INSERT INTO `creators` VALUES (475, NULL, NULL, 'حدیثه قربان', NULL, 'متولد: سال 1364 در تهران\r\nحدیثه قربان تصویرگر خلاق مجموعه سه جلدی بیزبیزپشه است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nدیپلم افتخار برای طراحی جلد مجلات یازدهمین جشنواره مطبوعات کودک و نوجوان، سالهای ۱۳۹۰ تا ۱۳۹۱، در بخش گرافیک مطبوعاتی\r\nدیپلم افتخار و لوح زرین هفدهمین جشنواره کتاب کودک و نوجوان، سال ۱۳۹۳\r\nدیپلم افتخار برای تصویرگری‌های چاپ شده در نشریات کودک و نوجوان، دوازدهمین جشنواره مطبوعات کودک و نوجوان ،  سال ۱۳۹۵', NULL, NULL, '2023-02-02 10:58:15', '2023-02-02 10:58:15', NULL, NULL);
