INSERT INTO `creators` VALUES (481, NULL, NULL, 'راحله برخورداری', NULL, 'متولد: سال 1358 در تهران\r\nراحله برخورداری تصویرگر خلاق کتاب کی باهوش‌تره از مجموعه قصه و نمایش آسان است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nتقدیر یونسکوunodc بخاطر طراحی پوستر،سال 1388\r\nشرکت در نمایشگاه گروهی تصویرگری در ایتالیا،سال 1388\r\nشرکت در نمایشگاه گروهی تصویرگری کانون در ایتالیا،سال 1387\r\nبرگزیده جشنواره تصویرگری جهان مقدس پادوآ ایتالیا،سال 1386\r\nشرکت در ورک شاپ کتاب فرانکفورت آلمان در خانه هنرمندان ایران،سال 1384\r\nنفر دوم نخستین جشنواره شاهکارهای ادبی با عنوان مولانا،سال 1386\r\nنفر دوم دومین و سومین جشنواره هنرهای تجسمی در رشته تصویرگری،سال 1382 و 1384\r\nنفر برگزیده در دومین و نفر سوم در سومین جشنواره تصویرگری کتاب های درسی،سال 1384 و 1386\r\nبرگزیده در دومین جایزه جهانی ادیان توحیدی،سال 1386\r\nنفر سوم جشنواره تصویرسازی و قصه گویی نشر علمی فرهنگی،سال 1390', NULL, NULL, '2023-02-02 10:58:16', '2023-02-02 10:58:16', NULL, NULL);
