INSERT INTO `creators` VALUES (522, NULL, NULL, 'سحر حقگو', '2', 'متولد: سال 1360 در رشت\r\nسحرحقگو تصویرگر خلاق کتاب شعر در خانۀ ما است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nبرگزیده در اولین جشنواره خیال ورویا زمستان،سال 1381\r\nمقام سوم در دومین جشنواره هنرهای تجسمی هنر جوان حوزه هنری(بخش تصویرسازی)، سال 1383\r\nمقام دوم در دومین جشنواره هنرهای تجسمی هنر جوان حوزه هنری(بخش تصویرسازی)، سال 1385\r\nمقام دوم در دومین جشنواره تصویرسازی کتب درسی، سال 1384\r\nمقام دوم در سومین جشنواره تصویرسازی کتب درسی (بخش کتابهای چاپ شده)،سال 1386\r\nمقام سوم در سومین جشنواره تصویرسازی کتب درسی (بخش کتابهای چاپ شده)، سال 1386\r\nمقام اول در سومین جشنواره تصویرسازی کتب درسی (بخش مولانا)، سال 1386\r\nبرگزیده در بخش تصویرسازی مجلات کانون پرورش فکری کودکان و نوجوانان،سال 1390\r\nبرگزیده در جشنواره تصویرسازی داستانهای قرآنی، سال 1383\r\nبرگزیده در جشنواره تصویرسازی انتشارات به نشر، سال 1389\r\nبرگزیده تصویرسازی روی جلد در جشنواره داخلی مجلات رشد، سال 1386', NULL, NULL, '2023-02-02 10:58:24', '2023-02-02 10:58:24', NULL, NULL);
