INSERT INTO `creators` VALUES (534, NULL, NULL, 'سمیه محمدی', '2', 'متولد: سال 1364 در تهران\r\n\r\nسمیه محمدی تصویرگر خلاق کتاب پیچ و هزارپا است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nهمکاری با نشریات و مجلات کودک و نوجوان در زمینه تصویرگری از سال ۹۱\r\nتصویرگری بیش از پنجاه عنوان کتاب، برای ناشرین داخلی و خارجی\r\nشرکت در چندین نمایشگاه گروهی تصویرگری در داخل و خارج از ایران\r\nراه یافته به فستیوال کودک و نوجوان سنگاپور\r\nمنتخب جشنواره آذری یزدی', NULL, NULL, '2023-02-02 10:58:27', '2023-02-02 10:58:27', NULL, NULL);
