INSERT INTO `creators` VALUES (552, NULL, NULL, 'طاهره ایبد', '2', 'متولد: سال 1342 در شیراز\nآثار:\nمجموعه بیزبیزپشه\nگرگ قرمزی\nنی‌نی خرسه\nماه من، ماه او\nکوچه بی کوچه\nرابین هود کوچک\nو...\nافتخارات:\nجشنواره ادبیات داستانی وزارت ارشاد، ۱۳۶۷، اثر برتر، شاخه شکسته\nکتاب سال مجله سروش نوجوان، ۱۳۷۵، اثر برگزیده، باغچه توی گلدان\nشورای کتاب کودک، ۱۳۷۵، اثر مناسب، باغچه توی گلدان\nجشنواره مطبوعات کانون پرورش فکری، ۱۳۷۵، اثر برگزیده، نخل سوخته\nجشنواره ادب پایداری وزارت ارشاد، ۱۳۷۹، اثر برگزیده، دور گردون\nکتاب سال کانون پرورش فکری، ۱۳۷۹، اثر برگزیده، چهل پنجره\nکتاب سال مجله سلام بچه‌ها، ۱۳۷۹، اثر برگزیده، چهل پنجره\nکتاب سال مجله سلام بچه‌ها، ۱۳۸۰، اثر برگزیده، به هوای گل سرخ\nجشنواره بیست سال، بیست نویسنده، ۱۳۸۰، اثر برگزیده، نویسنده برتر در حوزه نگارش، مسائل دختران', NULL, NULL, '2023-02-02 10:58:30', '2023-02-02 10:58:30', NULL, NULL);
