INSERT INTO `creators` VALUES (556, NULL, NULL, 'طیبه شامانی', '2', 'متولد: سال 1355 در دامغان\r\nآثار:\r\nخدا اینجا و اونجا\r\nآلزایمر\r\nقچ قچ\r\nمن بلدم بگم به\r\nپسته‌های بانمک\r\nو...\r\nافتخارات:\r\nنفر اول جشنواره کشوری یار و یادگار\r\nنفر اول جشنواره فجر، سال 1391\r\nنامزد کتاب سال دفاع مقدس با کتاب مسافرسپیده دم\r\nبرگزیده قلم زرین با کتاب دم گربه هارا بکش\r\nبرگزیده فجر و کتاب سال کانون با کتاب قچ قچ\r\nنامزد یازدهمین جشنواره مطبوعات\r\nبرنده لوح زرین دوازدهمین جشنواره مطبوعات', NULL, NULL, '2023-02-02 10:58:31', '2023-02-02 10:58:31', NULL, NULL);
