INSERT INTO `creators` VALUES (560, NULL, NULL, 'عاطفه ملکی جو', '2', 'متولد: سال 1357 در تهران\r\nعاطفه ملکی‌جو تصویرگر خلاق کتاب گریه نکن آقای گاو و نه قصه‌ی دیگر و... است که در نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nجایزه‌ی تشویقی اولین جشنواره شاهکارهای ادبی ایران، سال 1386\r\nبرگزیده ی چهاردهمین دوره جشنواره کتاب کودک و نوجوان بخش تصویرگری،(کتاب کشاورز و خروس_انتشارات کانون)، سال 1389\r\nنفر سوم اولین دوره جشنواره هنرهای تجسمی آزادگان، سال1389\r\nنفر اول سومین جشنواره مد و لباس فجر بخش تصویرگری، سال 1392\r\nنفر اول ششمین جشنواره بین المللی هنرهای تجسمی فجر بخش تصویرگری، سال 1392\r\nرتبه اول چهارمین مرحله استانی جشنواره هنری، سال 1392\r\nبرگزیده ی چهارمین و ششمین جشنواره هنرهای تجسمی فجر و ششمین دوسالانه تصویرگری تهران، سال 1392\r\nدارنده ی نشان آبان تصویرگری، سال 1396', NULL, NULL, '2023-02-02 10:58:32', '2023-02-02 10:58:32', NULL, NULL);
INSERT INTO `creators` VALUES (561, NULL, NULL, 'غزاله بیگدلو', NULL, 'متولد: سال 1359در تهران\r\nغزاله بیگدلو تصویرگر خلاق مجموعه شش جلدی نی‌نی‌ها و هولولو است که از نشر مهرک چاپ شده است.\r\n\r\nافتخارات:\r\nنمایشگاه‏های بین المللی مختلف که اثر او را در کاتالوگ خود چاپ کرده‏اند به شرح زیر است:\r\nشوخی\r\nبـرنـده‌ی جـایـزه‌ی قلـم زریـن سـال 2009 نمایشگاه بین‌المللی تصویرگری بلگراد\r\nبرگزیـده‌شـده‌ی سال 2012 ـ 2011 پنجـاه و ششمین نمایـشـگاه بین‌المللی کتابهای کودکان در موزه‌ی آفن‌باخ\r\nبرگزیده‌شده‌ی سال 2012 نمایشگاه تصویرگری کتاب کودک شارجه\r\nبرگزیده‌شده‌ی سال 2014 پنجمین نمایشگاه تصویرگری کروواسی\r\nبرگزیده شده‏ی سال 2015 نمایشگاه تصویرگری بلگراد\r\nبرگزیده‌شـده‌ی سـال 2010 سـومین فـسـتیوال جایـزه‌ی کتابـهـای تصویرگری‌شده‌ی سی‌جی کره برای تصویرگری\r\nبرگزیده‌شده‌ی سال 2011 نمایشگاه بین‌المللی تصویرگری بلگراد', NULL, NULL, '2023-02-02 10:58:32', '2023-02-02 10:58:32', NULL, NULL);
