INSERT INTO `creators` VALUES (592, NULL, NULL, 'کلر ژوبرت', '2', 'متولد: سال 1961 (میلادی) در پاریس\nآثار:\nچرا؟چرا؟چرا؟\nنذری پرپرک\nهدیه بادامک\nخط خطو\nآدم کوچولوی گرسنه\nو...\nافتخارات:\nکلر ژوبرت برای کتاب «در جستجوی خدا» دو جایزه‌ی جشنواره کتاب سلام و جشنواره کتاب‌های کمک آموزشی رشد را گرفت. کتاب «خداحافظ راکون پیر» از او هم سه جایزه‌ی جشنواره‌ی کتاب سلام، پروین اعتصامی و کتاب سال شهید غنی پور را برای این نویسنده به ارمغان آورد.', NULL, NULL, '2023-02-02 10:58:38', '2023-02-02 10:58:38', NULL, NULL);
