INSERT INTO `creators` VALUES (657, NULL, NULL, 'مهناز فتاحی', '2', 'متولد: سال 1347 در همدان\r\n\r\nآثار:\r\n\r\nفرنگیس\r\n\r\nلایکم کن\r\n\r\nعروس‌های جنگ\r\n\r\nباغ مادربزرگ\r\n\r\nقلب کوچک سپهر\r\n\r\nافتخارات:\r\n\r\nنفر اول گزارمان‌نویسی کشور در دو سال پیاپی\r\n\r\nکارگردان برگزیدۀ نمایش عروسکی کشور، سال1384\r\n\r\nموفقیت در جشنواره اداری شعر و داستان کشور\r\n\r\nبرگزیدهٔ داستان نوجوانان در جشنواره مطبوعات کودک و نوجوان کشور\r\n\r\nبرگزیدهٔ داستان نوجوان کتاب فصل کشور، سال1387\r\n\r\nنامزد کتاب سال جمهوری اسلامی با کتاب طعم تلخ خرما، سال 1387\r\n\r\nنامزد جایزه ادبی جلال آل احمد در سال 1394\r\n\r\nبرگزیده داستان نوجوان چهارمین دورۀ جایزه پروین اعتصامی، سال1389', NULL, NULL, '2023-02-02 10:58:57', '2023-02-02 10:58:57', NULL, NULL);
