INSERT INTO `creators` VALUES (695, NULL, NULL, 'کبری بابایی', '2', 'متولد: سال 1361 در تهران\nآثار:\nستاره‌ات گم شده\nمن آدم آهنی شدم\nلباس غول گم شدم\nگاو سفید گاو سیاه\nو....\nافتخارات:\nگام اول با کتاب \"ستاره‌ات گم شد\"\nپروین اعتصامی با کتاب \"ستاره ات گم شد\"\nپروین اعتصامی با کتاب \"من آدم آهنی شدم\"\nکاندید کتاب سال جمهوری اسلامی با کتاب \"من آدم آهنی شدم\"\nکتاب سال سلام بچه‌ها با کتاب \"ستاره‌ات گم شد\"\nکتاب سال سلام بچه‌ها با کتاب \"من آدم آهنی شدم\"\nجایزه جشنواره مطبوعات کودک و نوجوان', NULL, NULL, '2023-02-02 10:59:06', '2023-02-02 10:59:06', NULL, NULL);
INSERT INTO `creators` VALUES (696, NULL, NULL, 'زهرا کیقبادی', '2', 'زهرا کیقبادی\r\nتصویرگر\r\nمتولد: سال 1357در تهران\r\n\r\nافتخارات:\r\nنمایشگاه‌های گروهی خانه هنرمندان بهمن۱۳۹۲\r\nنمایشگاه فرهنگسرای آفتاب ۱۳۹۳\r\nنمایشگاه بین المللی محیط زیست ۱۳۹۲\r\nبرنده اول فراخوان انتشارات علمی فرهنگی ۱۳۹۳\r\nو ....', NULL, NULL, '2023-02-02 10:59:07', '2023-02-02 10:59:07', NULL, NULL);
INSERT INTO `creators` VALUES (697, NULL, NULL, 'گلنار ثروتیان', '2', 'متولد: سال 1356در تبریز\nافتخارات:\nاو در رقابت با کشورهای برزیل، روسیه، فنلاند، لهستان، ترکیه، مکزیک و ایتالیا در جشنوارهٔ دوسالانهٔ کارتون و تصویرسازی چین موفق به کسب جایزهٔ منتخب هیئت داوران شد.\nرتبهٔ اول تصویرگری در ششمین جشنوارهٔ هنرهای تجسمی ـ استان کردستان\nرتبهٔ قابل تقدیر تصویرگری در هشتمین جشنوارهٔ هنرهای تجسمی ـ استان فارس\nرتبهٔ قابل تقدیر پوستر در نهمین جشنوارهٔ هنرهای تجسمی اثر برگزیدهٔ و دیپلم افتخار اولین و دومین جشنوارهٔ بین المللی تصویرگری کتاب های درسی\nاثر منتخب هیئت داوران در دوسالانهٔ بین المللی مسترکاپ چین ۲۰۰۷\nاثر برگزیدهٔ در مسابقهٔ بین المللی کاریکاتور ترکیه با موضوع تعامل فرهنگی ۲۰۰۷\nپذیرش سه اثر در نمایشگاه بین المللی تصویرگری بلگراد و چاپ در کاتالوگ', NULL, NULL, '2023-02-02 10:59:07', '2023-02-02 10:59:07', NULL, NULL);
INSERT INTO `creators` VALUES (698, NULL, NULL, 'سمانه رهبرنیا', '2', 'متولد: سال 1361در تهران\r\nافتخارات:\r\nجشنوارهٔ ملی جوان،گالری صبا، تهران 1390\r\nهشتمین دوسالانه نقاشی ایران،1390\r\nنمایشگاه انفرادی نقاشی،گالری آن، تهران،1391\r\nنمایشگاه انفرادی تصویرسازی،گالری آرتین، مشهد1391\r\nبرگزاری ورکشاپ تصویرسازی،گالری آرتین، مشهد، 1391\r\nنمایشگاه انفرادی نقاشی،گالری آرتین، مشهد 1391\r\nنمایشگاه گروهی نقاشی،گالری آرته، تهران،1392\r\nنمایشگاه گروهی نقاشی،گالری لاله، تهران،1392\r\nنمایشگاه انفرادی نقاشی، گالری هوم، تهران 1392\r\nنمایشگاه گروهی تصویرسازی،گالری اکو، تهران1396\r\nنمایشگاه گروهی نقاشی،گالری پل ریچارد، لیون فرانسه ،2012\r\nنمایشگاه گروهی نقاشی،گالری سیحون، تهران،1397', NULL, NULL, '2023-02-02 10:59:07', '2023-02-02 10:59:07', NULL, NULL);
INSERT INTO `creators` VALUES (699, NULL, NULL, 'هاجر سلیمی', '2', 'متولد: سال 1361 در تهران\r\n\r\nافتخارات:\r\nاولین جشنواره بهار قرآن، مقام سوم، سال 1385\r\nنخستین نمایشگاه فضاسازی سوره های قرآن کریم، مقام برتر، سال 1385\r\nبیست و دومین جشنواره ملی قرآن دانشجویان ایران، مقام دوم، سال 1386\r\nجشنواره جهانی هنرهای تجسمی مقاومت، شرکت در بخش خارج از مسابقه، سال 1385\r\nدومین جایزه جهانی ادیان توحیدی ، شرکت در بخش خارج از مسابقه، سال 1386\r\nاولین دوسالانه تصویرگری نیایش، مقام دوم، سال 1388\r\nجشنواره ملی جوان ایرانی، شایسته تقدیر، سال 1389\r\nجشنواره هنرهای تجسمی آزادگان، شرکت در بخش خارج از مسابقه، سال 1389\r\nدومین دوسالانه تصویرگری نیایش، مقام سوم، سال 1391\r\nپژوهشگر برتر دانشگاه الزهرا، سال 1391\r\nپنجمین جشنواره هنرهای تجسمی فجر، شرکت در بخش خارج از مسابقه، سال 1391\r\nپژوهشگر برتر دانشگاه الزهرا، سال 1392\r\nبخش فونت نمايشگاه سرو نقره‌ای ، مقام نخست، سال 1393\r\nسومین جشنواره جهانی هنر مقاومت،مقام دوم، سال 1393\r\nپژوهشگر برتر دانشگاه الزهرا، سال 1393', NULL, NULL, '2023-02-02 10:59:07', '2023-02-02 10:59:07', NULL, NULL);
