INSERT INTO `creators` VALUES (703, NULL, NULL, 'انتشار داستان‌های زندگی حضرت زینب(س) برای کودکان و نوجوانان', '2', '<div class=\"item-header\">\n<div class=\"item-title\">\n<h1 class=\"title\"><strong><span style=\"font-size: 16px\">به گزارش مهرک به نقل از <a href=\"https://www.irna.ir/news/84610846/%D8%A7%D9%86%D8%AA%D8%B4%D8%A7%D8%B1-%D8%AF%D8%A7%D8%B3%D8%AA%D8%A7%D9%86-%D9%87%D8%A7%DB%8C-%D8%B2%D9%86%D8%AF%DA%AF%DB%8C-%D8%AD%D8%B6%D8%B1%D8%AA-%D8%B2%DB%8C%D9%86%D8%A8-%D8%B3-%D8%A8%D8%B1%D8%A7%DB%8C-%DA%A9%D9%88%D8%AF%DA%A9%D8%A7%D9%86\"><em>خبرگزاری ایرنا</em></a>، </span><em style=\"font-size: 16px\">بانوی قصه من </em><span style=\"font-size: 16px\">در ۱۲ فصل به فرازهای مختلف زندگی این بانوی اسلام پرداخته است؛ </span><em style=\"font-size: 16px\">از بدو تولد تا ماجرای حزن‌انگیز کربلا و شهادت برادر ایشان؛ من دایه‌ی بانو زینبم، من همسایه‌ی بانو زینبم، من در شهر کوفه شاگرد بانو زینبم، من پسر بانو زینبم، من همسر امام حسینم، من دختر امام حسینم، من خواهر امام حسینم، من همسر یزدیم، من نماینده‌ی سرزمین روم در شام هستم، من همسر زینبم، من خلیفه بزرگم پسر معاویه و من قاتل نوه‌ی پیامبرم </em><span style=\"font-size: 16px\">نام فصل‌های این کتاب است و در هر فصل یک شخصیت داستانی را روایت می‌کند که بانو زینب(س) در آن حضور دارد و به یک بعد از شخصیت ایشان پرداخته می‌شود.</span></strong></h1>\n</div>\n</div>\n<div class=\"item-body\">\n<div class=\"item-text\">\n\nدر بخشی از این کتاب می‌خوانیم:\n\nدهم محرم بود. خورشید خسته و گداخته به کنار آسمان رسیده بود. مادر صحرای کربلا بودیم. عمه در میان کشته‌ها ایستاده بود. همه دورش بودند. بچه‌ها گریه می‌کردند، از سربازهای دشمن می‌ترسیدند. نمی‌دانستیم دیگر می‌خواهند چه بلاهایی بر سرمان بیاورند.\n\nهوا هنوز بوی خون می‌داد. پیکرهای پاک عزیزانمان در گوشه گوشه‌ی خاک به چشم می‌خورد. عمرسعد فرمان داده بود تا سرهای شهیدان را جدا کنند. به عمه‌ام نگاه کردم. همه می‌دانستیم که او نیمه دیگر پدرم است...\n\nفصل‌های این کتاب با احادیثی از اهل بیت(ع) خطاب به  حضرت زینب(س) آغاز و در ادامه داستانی با همان مضمون روایت شده است.\n\n<em>بانوی قصه من </em>شانزدهمین جلد از مجموعه<em> قصه نیکان</em> است که به قلم نویسندگان مختلف برای گروه سنی کودک و نوجوان نوشته شده و به زندگی ائمه و پیامبر اکرم(ع) می‌پردازد.\n\nاین کتاب در ۱۳۷ صفحه و با قیمت ۴۰ هزار تومان روانه بازار نشر شده است.\n\n</div>\n</div>', NULL, NULL, '2023-02-02 10:59:08', '2023-02-02 10:59:08', NULL, NULL);
