INSERT INTO `products` VALUES (150, NULL, NULL, NULL, 'هشتگ داستان', '%d9%87%d8%b4%d8%aa%da%af-%d8%af%d8%a7%d8%b3%d8%aa%d8%a7%d9%86', NULL, '', '<p>این مجموعه توسط هفت بانوی نویسنده در گروه سنی ۱۴ تا ۲۱ سال نوشته شده. بیان دغدغه‌های سال‌های نوجوانی، بلوغ، مدرسه، تعامل با گروه همسالان، آمادگی برای کنکور و ورود به دانشگاه، بحران‌های روابط خانوادگی و چالش‌های خواهر و برادری از مهم‌ترین مضامین این کتاب به‌شمار می‌رود.</p>\n', NULL, 45000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:29', '2023-01-23 12:20:29', NULL, NULL);
