INSERT INTO `products` VALUES (179, NULL, NULL, NULL, 'مشق آسمان', '%d9%85%d8%b4%d9%82-%d8%a2%d8%b3%d9%85%d8%a7%d9%86', NULL, '', '<p>کتاب که حتما نباید مربع مستطیل باشد. بعضی کتاب‌ها مثل گل هستند، گل‌هایی که گلبرگ‌های معطر و خوشرنگی از شعر دارند. مثل کتاب مشق آسمان.</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:32', '2023-01-23 12:20:32', NULL, NULL);
INSERT INTO `products` VALUES (180, NULL, NULL, NULL, 'من گل سرخم تو گل زردی', '%d9%85%d9%86-%da%af%d9%84-%d8%b3%d8%b1%d8%ae%d9%85-%d8%aa%d9%88-%da%af%d9%84-%d8%b2%d8%b1%d8%af%db%8c', NULL, '', '<p>اگر می‌خواهید دست بزنید و شعر بخوانید زودتر این کتاب را بردارید!</p>\n', NULL, 32000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:32', '2023-01-23 12:20:32', NULL, NULL);
INSERT INTO `products` VALUES (181, NULL, NULL, NULL, 'من هنوز یک پرنده‌ام', '%d9%85%d9%86-%d9%87%d9%86%d9%88%d8%b2-%db%8c%da%a9-%d9%be%d8%b1%d9%86%d8%af%d9%87%d8%a7%d9%85', NULL, '', '<p>کتاب من هنوز یک پرنده‌ام، داستان پسری معلول به نام فرزاد را روایت می‌کند که قرار است در یک نمایشنامه بازی کند اما به دلیل مشکل پاهایش از بازی کردن در نمایش گریزان است.</p>\n', NULL, 0, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:33', '2023-01-23 12:20:33', NULL, NULL);
INSERT INTO `products` VALUES (182, NULL, NULL, NULL, 'معلم مورچه‌ها', '%d9%85%d8%b9%d9%84%d9%85-%d9%85%d9%88%d8%b1%da%86%d9%87%d9%87%d8%a7', NULL, '', '<p>اگر دوست دارید 10 شعر خوب بخوانید حتما این کتاب را انتخاب کنید.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:33', '2023-01-23 12:20:33', NULL, NULL);
INSERT INTO `products` VALUES (183, NULL, NULL, NULL, 'من عروسک گلهانم', '%d9%85%d9%86-%d8%b9%d8%b1%d9%88%d8%b3%da%a9-%da%af%d9%84%d9%87%d8%a7%d9%86%d9%85', NULL, '', '<p>نمی‌دانم در مورد کشتی صلح چیزی شنیده‌اید یا نه! اما حتما نام غزه را شنیده‌اید. اگر می‌خواهید بدانید صهیونیست‌ها چطور جلوی کشتی صلح را گرفتند و چرا کشتی مرمره یا همان کشتی صلح نتوانست به مردم در محاصره غزه کمک کند حتما این کتاب را بخوانید.</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:33', '2023-01-23 12:20:33', NULL, NULL);
INSERT INTO `products` VALUES (184, NULL, NULL, NULL, 'مرغ شل', '%d9%85%d8%b1%d8%ba-%d8%b4%d9%84', NULL, '', '<p>پدر حبیب با قرض و وام یک وانت می‌خرد تا با آن کار کند، مادر هم اصرار دارد که طبق یک رسم و سنت قدیمی برای ماشین جدید قربانی کنند. از آنجایی که پدر توان خرید گوسفند را ندارد قرار می‌شود یک خروس بخرد اما در نهایت با یک مرغ به خانه بازمی‌گردد که آن هم شَل از آب در می‌آید!</p>\n', NULL, 60000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:33', '2023-01-23 12:20:33', NULL, NULL);
INSERT INTO `products` VALUES (185, NULL, NULL, NULL, 'مردان نخیله', '%d9%85%d8%b1%d8%af%d8%a7%d9%86-%d9%86%d8%ae%db%8c%d9%84%d9%87', NULL, '', '<p>مردان نخیله روایت مردمان کوفی است. مردمانی که نان به نرخ روز می‌خورند و ایمانشان در گرو زر و سکه است. قهرمانان قصه عامر و قاسم نوجوانانی هستند که در شمشیرزنی از مهارت بالایی برخوردارند و باید در غیاب پدران خویش که به میدان کارزار رفته اند، به دنبال تمیز دادن حقیقت از شایعات و حمایت از خانواده و مظلومان باشند.</p>\n', NULL, 30000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:33', '2023-01-23 12:20:33', NULL, NULL);
INSERT INTO `products` VALUES (186, NULL, NULL, NULL, 'کاکل به سر', '%da%a9%d8%a7%da%a9%d9%84-%d8%a8%d9%87-%d8%b3%d8%b1', NULL, '<p>این مجموعه شامل 10 شعر است که شخصیت اصلی آن‌ها کاکل به سر، خروس مزرعه است.</p>\n', '<p>کاکل به سر خروسی است که میخواهد تاج داشته باشد!</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:33', '2023-01-23 12:20:33', NULL, NULL);
INSERT INTO `products` VALUES (187, NULL, NULL, NULL, 'کلاغ سیاه', '%da%a9%d9%84%d8%a7%d8%ba-%d8%b3%db%8c%d8%a7%d9%87', NULL, '<p>این مجموعه شامل 10 شعر است که شخصیت اصلی آن‌ها کلاغ سیاه است.</p>\n', '<p>کلاغ سیاه میخواهد سلام بدهد ولی می‌گوید قارقار!</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (188, NULL, NULL, NULL, 'ناجی کاروان', '%d9%86%d8%a7%d8%ac%db%8c-%da%a9%d8%a7%d8%b1%d9%88%d8%a7%d9%86', NULL, '', '<p>پهلوان عبدالله و همراهانش از کربلا بازمی‌گشتند که در راه به کاروانی که عزم کربلا را داشتند می‌خورند. در آن کاروان فردی است که ادعای زور و بازو دارد و پهلوان را دعوت به مبارزه می‌کند اما پهلوان نمی‌پذیرد. شب دزدها به کاروان حمله می کنند. باقی ماجرا را باید در کتاب بخوانید.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (189, NULL, NULL, NULL, 'والی دادگر', '%d9%88%d8%a7%d9%84%db%8c-%d8%af%d8%a7%d8%af%da%af%d8%b1', NULL, '', '<p>پهلوان اسد کرمانی از طرف شاه‌ شجاع، والی کرمان می‌شود و به عدل و داد حکمرانی می‌کند. اما این دادگری به مذاق عده‌ای خوش نمی‌آید و دردسرهایی برای پهلوان درست می‌کند.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (190, NULL, NULL, NULL, 'مجموعه‌ای از مهربانی', '%d9%85%d8%ac%d9%85%d9%88%d8%b9%d9%87%d8%a7%db%8c-%d8%a7%d8%b2-%d9%85%d9%87%d8%b1%d8%a8%d8%a7%d9%86%db%8c', NULL, '', '<p>اگر خوب گوش بدهید این کتاب همه مهربانی‌های دنیا را به زبان شعر در گوش شما زمزمه می‌کند.</p>\n', NULL, 10000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (191, NULL, NULL, NULL, 'هویجوری', '%d9%87%d9%88%db%8c%d8%ac%d9%88%d8%b1%db%8c', NULL, '<p>این مجموعه شامل 10 شعر است که شخصیت اصلی آن‌ها خرگوشی به نام «هویجوری» است.</p>\n', '<p>ده شعر برای هویجوری خرگوشه!</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (192, NULL, NULL, NULL, 'گاو کوچولو', '%da%af%d8%a7%d9%88-%da%a9%d9%88%da%86%d9%88%d9%84%d9%88', NULL, '<p>این مجموعه شامل 10 شعر است که شخصیت اصلی آن‌ها گاو کوچولو است.</p>\n', '<p>تا حالا فکر کردی اگر یک گاو کوچولو داشتی چکار می‌کردی؟</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (193, NULL, NULL, NULL, 'جهان پهلوان', '%d8%ac%d9%87%d8%a7%d9%86-%d9%be%d9%87%d9%84%d9%88%d8%a7%d9%86', NULL, '', '<p>این کتاب روایت کسب اولین مدال طلای غلامرضا تختی است. روزی که نشان داد تختی قهرمان و پهلوانی مردمی است.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (194, NULL, NULL, NULL, 'بچه‌های کوهستان', '%d8%a8%da%86%d9%87%d9%87%d8%a7%db%8c-%da%a9%d9%88%d9%87%d8%b3%d8%aa%d8%a7%d9%86', NULL, '<p>او همراه با دوتن دیگر از اسیران، هفده روز را با پای پیاده طی می‌کند تا به خاک ایران می‌رسد.</p>\n', '<p>کتاب بچه‌های کوهستان خاطرات سید رضا موسوی به قلم احمد دهقان است که در سنین جوانی به اسارت نیروهای بعثی عراق در آمده و پس از چندی برای فرار از زندان عراق تلاش می کند.</p>\n', NULL, 30000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (195, NULL, NULL, NULL, 'رسم پهلوانی', '%d8%b1%d8%b3%d9%85-%d9%be%d9%87%d9%84%d9%88%d8%a7%d9%86%db%8c', NULL, '', '<p>پوریای ولی نماد پهلوانی‌ است. این کتاب حکایتی از حکایات جوانمردی پوریای ولی را بازگو می‌کند.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:34', '2023-01-23 12:20:34', NULL, NULL);
INSERT INTO `products` VALUES (196, NULL, NULL, NULL, 'بچه‌های عملیات کرکوک', '%d8%a8%da%86%d9%87%d9%87%d8%a7%db%8c-%d8%b9%d9%85%d9%84%db%8c%d8%a7%d8%aa-%da%a9%d8%b1%da%a9%d9%88%da%a9', NULL, '<p>او که بیسیمچی بوده، به همراه گروه‌های چریکی 150 کیلومتر در داخل خاک عراق نفوذ می‌کنند و پس از عملیات، با پای پیاده به وطن بازمی‌گردند.</p>\n', '<p>کتاب «بچه های عملیات کرکوک» خاطرات یحیی نیازی است از عملیاتی در داخل خاک عراق.</p>\n', NULL, 30000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (197, NULL, NULL, NULL, 'مرد بیدار', '%d9%85%d8%b1%d8%af-%d8%a8%db%8c%d8%af%d8%a7%d8%b1', NULL, '', '<p>کتاب مصور حاضر، داستان پهلوانی به نام مازار است که در پیری همراه کاروانی به زیارت امام رضا(ع) می‌رود. او با وجود سن و سال زیادش ناجی کاروانیان می‌شود. اگر می‌خواهید بدانید چگونه؟ این کتاب را بردارید و بخوانید.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (198, NULL, NULL, NULL, 'پهلوان شیردل', '%d9%be%d9%87%d9%84%d9%88%d8%a7%d9%86-%d8%b4%db%8c%d8%b1%d8%af%d9%84', NULL, '', '<p>پهلوان شیردل به مصاف شیر می‌رود.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (199, NULL, NULL, NULL, 'جوانمرد', '%d8%ac%d9%88%d8%a7%d9%86%d9%85%d8%b1%d8%af', NULL, '', '<p>پهلوانی به زور و بازو نیست. پهلوانی منشِ جوانمردی است. منشِ ایستادن جلوی زورگویی. حکایت این کتاب هم حکایت یکی از این پهلوانانِ جوانمرد است.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (200, NULL, NULL, NULL, 'پهلوان سربدار', '%d9%be%d9%87%d9%84%d9%88%d8%a7%d9%86-%d8%b3%d8%b1%d8%a8%d8%af%d8%a7%d8%b1', NULL, '', '<p>حتما از عیاران و سربداران چیزهایی شنیده‌اید. اگر می‌خواهید مختصری از شرایط آن روزگار ایران بدانید این کتاب را بردارید و بخوانید. زیاد وقتتان را نمی‌گیرد.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (201, NULL, NULL, NULL, 'پیلتن', '%d9%be%db%8c%d9%84%d8%aa%d9%86', NULL, '', '<p>پهلوان کسی‌ است که دیگران را بر خود مقدم بدارد. اگر می‌خواهید داستانی از یکی از این پهلوانان بخوانید این کتاب را مطالعه کنید. آیا قصه فیل و موش را شنیده‌اید؟ این کتاب داستان مواجه این دو با هم است.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (202, NULL, NULL, NULL, 'بچه‌های تپه سوم', '%d8%a8%da%86%d9%87%d9%87%d8%a7%db%8c-%d8%aa%d9%be%d9%87-%d8%b3%d9%88%d9%85', NULL, '<p>او با مشقات فراوان موفق می‌شود که به جبهه وارد شود و بعد از مدتی با تنی مجروح، در عملیات والفجر 3 بیش از دو هفته در بیشه‌ای به سر می‌برد. آن‌ها سه نوجوان بودند. خاطرات همدلی این سه نوجوان در دوران جنگ، شما را با تصویر واقعی‌تری از جنگ آشنا می کند.</p>\n', '<p>بچه‌های تپه سوم خاطرات سیدحمیدرضا طالقانی است که با روایتی جذاب از اتفاقی هول‌انگیز حکایت می‌کند.</p>\n', NULL, 30000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (203, NULL, NULL, NULL, 'بچه‌های آوه زین', '%d8%a8%da%86%d9%87%d9%87%d8%a7%db%8c-%d8%a2%d9%88%d9%87-%d8%b2%db%8c%d9%86', NULL, '<p>فرنگ با رضایت خودش، به کارهای سخت مردانه مثل کارگری در مزارع و حمل گونی‌های سنگین، ساخت خانه، آهنگری و تفنگ سازی، تن می‌دهد و مایه شگفتی دیگران را فراهم می‌سازد. وقتی جنگ تحمیلی شروع می‌شود، از رفتن او به میدان جنگ، ممانعت به عمل‌ می‌آید اما او با ماندنش در روستا هم سلحشوری‌هایی به خرج می‌دهد که کمتر کسی آن‌ها را باور می‌کند. در این کتاب با شجاعت‌های فرنگیس، دختری از دیار کرمانشاه آشنا می‌شوید و خواه ناخواه او را تحسین می‌کنید.</p>\n', '<p>فرنگیس یا فرنگ که از کودکی سر نترسی دارد، در موقعیت‌های خطرناک، جسارت و شجاعت زیادی از خود بروز می‌دهد، به طوری که همه را انگشت به دهان می گذارد.</p>\n', NULL, 30000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (204, NULL, NULL, NULL, 'مثل من به انتظار', '%d9%85%d8%ab%d9%84-%d9%85%d9%86-%d8%a8%d9%87-%d8%a7%d9%86%d8%aa%d8%b8%d8%a7%d8%b1', NULL, '', '<p>به نظر شما شاعرها مثل چه کسانی انتظار می‌کشند و منتظر چه کسی هستند؟ کتاب مثل من به انتظار به ما جواب می‌دهد.</p>\n', NULL, 13000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:35', '2023-01-23 12:20:35', NULL, NULL);
INSERT INTO `products` VALUES (205, NULL, NULL, NULL, 'مثل همیشه‌ای کاش', '%d9%85%d8%ab%d9%84-%d9%87%d9%85%db%8c%d8%b4%d9%87%d8%a7%db%8c-%da%a9%d8%a7%d8%b4', NULL, '', '<p>شما هم خیال پردازید؟ من که فکر می‌کنم سالمون آخر خیال پردازیست. او با خیال‌هایش و در خیال‌هایش زندگی می‌کند.</p>\n', NULL, 6000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (206, NULL, NULL, NULL, 'ما برگزیدگان', '%d9%85%d8%a7-%d8%a8%d8%b1%da%af%d8%b2%db%8c%d8%af%da%af%d8%a7%d9%86', NULL, '', '<p>گفت‌وگوهایی دلنشین و آرام با چاشنی منطق و استدلال.</p>\n', NULL, 20000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (207, NULL, NULL, NULL, 'ماشو در مه', '%d9%85%d8%a7%d8%b4%d9%88-%d8%af%d8%b1-%d9%85%d9%87', NULL, '<p>ماشو، کاظم و سیا هر روز زیر لوله‌های شرکت نفت را می‌جویند تا ساک پر از طلا و جواهر را بیابند و زندگی و رویاهایشان را سر و سامانی بدهند. پس از مشقات فراوان بالاخره ساک را می‌یابند.</p>\n', '<p>فرهاد حسن زاده، نامزد ایرانی جایزه آسترید لیندگرن و هانس کریستین آندرسن، در این کتاب به روایت داستانی می‌پردازد که قهرمان و کاراکتر اصلی آن پسر نوجوانی به نام ماشو (ماشو مخفف ماشاءالله) است.</p>\n', NULL, 18000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (208, NULL, NULL, NULL, 'لحظه‌های ساکت پیاده رو', '%d9%84%d8%ad%d8%b8%d9%87%d9%87%d8%a7%db%8c-%d8%b3%d8%a7%da%a9%d8%aa-%d9%be%db%8c%d8%a7%d8%af%d9%87-%d8%b1%d9%88', NULL, '', '<p>بهترین شعرها را می‌توان در لحظه‌های ساکت پیاده‌رو زمزمه کرد، پیاده‌روهایی که تنهایی ما را همراهی می‌کنند.</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (209, NULL, NULL, NULL, 'مثل رود پر خروش', '%d9%85%d8%ab%d9%84-%d8%b1%d9%88%d8%af-%d9%be%d8%b1-%d8%ae%d8%b1%d9%88%d8%b4', NULL, '', '<p>مثل رود پر خروش روایت دغدغه‌های نوجوانی است در خانه و مدرسه و لحظات پرکشش و جذابی را تجربه می‌کند که خواند این داستان را بسیار لذت بخش می‌کند.</p>\n', NULL, 0, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (210, NULL, NULL, NULL, 'مثل سه تا', '%d9%85%d8%ab%d9%84-%d8%b3%d9%87-%d8%aa%d8%a7', NULL, '', '<p>امروز وقتی پدرم به خانه آمد سه تا بلیط در دستش بود، سه تا بلیط که قرار است من و بابا و مامان را به سه کبوتر تبدیل کند.</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (211, NULL, NULL, NULL, 'مادرم پروانه است', '%d9%85%d8%a7%d8%af%d8%b1%d9%85-%d9%be%d8%b1%d9%88%d8%a7%d9%86%d9%87-%d8%a7%d8%b3%d8%aa', NULL, '<p>بیان احساسات کودکانه، ستایش مادر و عشق او به فرزند، سایه‌روشنی از رؤیاهای کودکی، دوستی با طبیعت و مهرورزی از مهم‌ترین مضامین شعرهای این کتاب به‌شمار می‌رود. این کتاب 12 شعر لطیف کودکانه را در قالب سه‌پاره و چهارپاره در خود جای داده و خواننده را با خود به سرزمین رؤیاها می‌برد.</p>\n', '<p>«مادرم پروانه است» عنوان یک اثر هنری زیبا و جذاب، آمیخته‌ای از هنر تصویرآفرینی و شعر است. کلمات در پیوند با رنگ‌ها و تصاویر، مجموعه‌ای منحصر به ‌فرد از شعر کودک را در برابر دیدگان شما قرار می‌دهد.</p>\n', NULL, 37000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (212, NULL, NULL, NULL, 'مامان چقدر خوب است', '%d9%85%d8%a7%d9%85%d8%a7%d9%86-%da%86%d9%82%d8%af%d8%b1-%d8%ae%d9%88%d8%a8-%d8%a7%d8%b3%d8%aa', NULL, '<p>مادر، موضوع بیشتر شعرهای این کتاب است که به زبانی ساده و کودکانه، آن هم با ریتم و آهنگی دلپذیر به نظم درآمده است. بیان دغدغه‌های کودکی و توصیف احساسات این دوران نیز از مضامین دیگر کتاب به ‌شمار می‌رود.</p>\n', '<p>شعرها بخشی جدایی‌ناپذیر از دنیای کودکی هستند و حتی برخی از آن‌ها تا سال‌ها پس از بزرگسالی نیز در حافظه و یادها ماندگار خواهند بود. «مامان چه‌قدر خوب است» دفتر شعری است سروده‌ی ناصر کشاورز.</p>\n', NULL, 10000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (213, NULL, NULL, NULL, 'ماهی عروس نداره', '%d9%85%d8%a7%d9%87%db%8c-%d8%b9%d8%b1%d9%88%d8%b3-%d9%86%d8%af%d8%a7%d8%b1%d9%87', NULL, '', '<p>این کتاب را باز کن و آرام پایت را در حوض ماهی‌ها بگذار و با ماهی‌ها شعر بخوان.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:36', '2023-01-23 12:20:36', NULL, NULL);
INSERT INTO `products` VALUES (214, NULL, NULL, NULL, 'گم بی', '%da%af%d9%85-%d8%a8%db%8c', NULL, '<p>اگر قول بدهند زود بیایند و مرا پیدا کنند من هم قول می‌دهم آن‌ها را ببخشم. راستش را بخواهید خیلی دلم برایشان تنگ شده. به نظر شما پدر و مادر یک گُم بی کجا می‌توانند رفته باشند؟</p>\n', '<p>درست است که این آقای پلیس خیلی مهربان است و برایم پفک و دمپایی و آدامس خریده و دارد با من بازی می‌کند. درست است از دستشان ناراحتم و فکر می‌کنم مرا گم کرده‌اند ولی بالاخره پدر و مادرم هستند.</p>\n', NULL, 28000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:37', '2023-01-23 12:20:37', NULL, NULL);
INSERT INTO `products` VALUES (215, NULL, NULL, NULL, 'گوسفندی برای شاه', '%da%af%d9%88%d8%b3%d9%81%d9%86%d8%af%db%8c-%d8%a8%d8%b1%d8%a7%db%8c-%d8%b4%d8%a7%d9%87', NULL, '<p>این مجموعه داستان به کوشش خسرو باباخانی و رقیه‌سادات صفوی گردآوردی شده است.</p>\n', '<p>داستان‌های کوتاه و خواندنی که در تب و تاب سال‌های قبل انقلاب می‌گذرد و هرکدام روایت‌گر حال و احوال مردم این زمانه را دارد. دوره ارباب و رعیت‌ها، خیزش مردمی و آگاهی، کم کم مردم را با واژه انقلاب آشنا می‌کند.</p>\n', NULL, 17000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:37', '2023-01-23 12:20:37', NULL, NULL);
INSERT INTO `products` VALUES (216, NULL, NULL, NULL, 'لانه‌ی گنجشک‌ها', '%d9%84%d8%a7%d9%86%d9%87%db%8c-%da%af%d9%86%d8%ac%d8%b4%da%a9%d9%87%d8%a7', NULL, '', '<p>اگر تا حالا گنجشک‌ها شما را به لانه خود دعوت نکرده‌اند این کتاب را بردارید و لانه آن‌ها را از نزدیک ببینید.</p>\n', NULL, 24000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:37', '2023-01-23 12:20:37', NULL, NULL);
INSERT INTO `products` VALUES (217, NULL, NULL, NULL, 'گنجشک و پنبه دانه', '%da%af%d9%86%d8%ac%d8%b4%da%a9-%d9%88-%d9%be%d9%86%d8%a8%d9%87-%d8%af%d8%a7%d9%86%d9%87', NULL, '', '<p>تا حالا داستان قافیه‌دار خوانده‌اید؟ این داستان قافیه‌دار گنجشکی‌ است که پنبه‌دانه‌ای را به کشاورز می‌دهد تا برایش بکارد و در عوض زحمتش نیمی از آن را بردارد. این ماجرا ادامه پیدا می‌کند تا این که شیطان گنجشک را گول می زند.</p>\n', NULL, 21000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:37', '2023-01-23 12:20:37', NULL, NULL);
INSERT INTO `products` VALUES (218, NULL, NULL, NULL, 'لالایی‌های کودکان ایران', '%d9%84%d8%a7%d9%84%d8%a7%db%8c%db%8c%d9%87%d8%a7%db%8c-%da%a9%d9%88%d8%af%da%a9%d8%a7%d9%86-%d8%a7%db%8c%d8%b1%d8%a7%d9%86', NULL, '', '<p>آخرین باری که با لالایی مادر به خواب رفتید را یادتان هست؟ بیایید در این کتاب دنبال آخرین لالایی با صدای مادرمان بگردیم.</p>\n', NULL, 33000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:37', '2023-01-23 12:20:37', NULL, NULL);
INSERT INTO `products` VALUES (219, NULL, NULL, NULL, 'گل خاکی', '%da%af%d9%84-%d8%ae%d8%a7%da%a9%db%8c', NULL, '', '<p>کتاب گل خاکی دربردارنده‌ی مجموعه داستان‌هایی کوتاه به قلم حبیب غنی‌پور است و شما را به روزگار پر التهابی می‌برد که مردمانی جسور با ایثار و از خودگذشتگی از کشور عزیزمان دفاع کردند.</p>\n', NULL, 16000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:37', '2023-01-23 12:20:37', NULL, NULL);
INSERT INTO `products` VALUES (220, NULL, NULL, NULL, 'گرگ و میش', '%da%af%d8%b1%da%af-%d9%88-%d9%85%db%8c%d8%b4', NULL, '', '<p>یکی از اتفاقاتی که بعد از ظهور امام زمان(عج)‌ می‌افتد این است که گرگ و میش در کنار هم با صلح و آرامش زندگی می‌کنند.</p>\n', NULL, 10000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:37', '2023-01-23 12:20:37', NULL, NULL);
INSERT INTO `products` VALUES (221, NULL, NULL, NULL, 'لپ قرمزی', '%d9%84%d9%be-%d9%82%d8%b1%d9%85%d8%b2%db%8c', NULL, '', '<p>لپ قرمزی داستان دختری قالی‌باف، مهربان و روستایی‌ است که با نو‌ه خان دوست می‌شود. لپ قرمزی به ترانه قالی‌بافی یاد می‌دهد. با هم روی پشت بام می‌خوابند و کلی ماجرا با هم دارند اما ترانه به شهر می‌رود و &#8230; .اگر می‌خواهید ماجرای این دوستی را بدانید کتاب را بردارید و بخوانید.‌</p>\n', NULL, 13000, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:38', '2023-01-23 12:20:38', NULL, NULL);
INSERT INTO `products` VALUES (222, NULL, NULL, NULL, 'گرداب سکندر', '%da%af%d8%b1%d8%af%d8%a7%d8%a8-%d8%b3%da%a9%d9%86%d8%af%d8%b1', NULL, '', '<p>سرزمین ما سرشار از افسانه‌ها و قصه‌های پرکشش و جذاب است. هر شهر و دیار این سرزمین حس و حال و اتمسفر خودش را دارد. جنوب و جاشوهایش پر از حوادث و قصه‌های جذابی هستند که خواندن آن‌ها سرشار از لذت است.</p>\n', NULL, 0, NULL, NULL, 1, '1', '1', NULL, NULL, 1, NULL, 0, 1, 1, 1, 0, 50, NULL, NULL, NULL, '2023-01-23 12:20:38', '2023-01-23 12:20:38', NULL, NULL);
